import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { runAllCRUDTests } from '@/lib/testing/crudTesting';
import { Loader2, Play, CheckCircle, XCircle, Clock } from 'lucide-react';

interface TestSuite {
  suiteName: string;
  results: any[];
  totalDuration: number;
  passed: number;
  failed: number;
}

export default function DatabaseTestPanel() {
  const [isRunning, setIsRunning] = useState(false);
  const [testResults, setTestResults] = useState<TestSuite[]>([]);
  const [lastRun, setLastRun] = useState<Date | null>(null);

  const handleRunTests = async () => {
    setIsRunning(true);
    try {
      const results = await runAllCRUDTests();
      setTestResults(results);
      setLastRun(new Date());
    } catch (error) {
      console.error('Test execution failed:', error);
    } finally {
      setIsRunning(false);
    }
  };

  const getTotalStats = () => {
    const totalPassed = testResults.reduce((sum, suite) => sum + suite.passed, 0);
    const totalFailed = testResults.reduce((sum, suite) => sum + suite.failed, 0);
    const totalDuration = testResults.reduce((sum, suite) => sum + suite.totalDuration, 0);

    return { totalPassed, totalFailed, totalDuration };
  };

  const { totalPassed, totalFailed, totalDuration } = getTotalStats();

  return (
    <Card className="w-full max-w-4xl mx-auto">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <CheckCircle className="h-5 w-5" />
          Database Integrity Testing
        </CardTitle>
        <CardDescription>
          Test all CRUD operations and validate data integrity constraints
        </CardDescription>
      </CardHeader>

      <CardContent className="space-y-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-4">
            <Button 
              onClick={handleRunTests} 
              disabled={isRunning}
              className="flex items-center gap-2"
            >
              {isRunning ? (
                <Loader2 className="h-4 w-4 animate-spin" />
              ) : (
                <Play className="h-4 w-4" />
              )}
              {isRunning ? 'Running Tests...' : 'Run All Tests'}
            </Button>

            {lastRun && (
              <div className="flex items-center gap-2 text-sm text-muted-foreground">
                <Clock className="h-4 w-4" />
                Last run: {lastRun.toLocaleTimeString()}
              </div>
            )}
          </div>

          {testResults.length > 0 && (
            <div className="flex items-center gap-2">
              <Badge variant={totalFailed === 0 ? "default" : "destructive"}>
                {totalPassed}/{totalPassed + totalFailed} Passed
              </Badge>
              <Badge variant="outline">
                {totalDuration}ms
              </Badge>
            </div>
          )}
        </div>

        {testResults.length > 0 && (
          <div className="space-y-4">
            <h3 className="text-lg font-semibold">Test Results</h3>

            {testResults.map((suite, index) => (
              <Card key={index} className="border-l-4 border-l-primary">
                <CardHeader className="pb-3">
                  <div className="flex items-center justify-between">
                    <CardTitle className="text-base">{suite.suiteName}</CardTitle>
                    <div className="flex items-center gap-2">
                      <Badge variant={suite.failed === 0 ? "default" : "destructive"}>
                        {suite.passed}/{suite.passed + suite.failed}
                      </Badge>
                      <Badge variant="outline">
                        {suite.totalDuration}ms
                      </Badge>
                    </div>
                  </div>
                </CardHeader>

                <CardContent>
                  <div className="space-y-2">
                    {suite.results.map((result, resultIndex) => (
                      <div 
                        key={resultIndex}
                        className="flex items-center justify-between p-2 rounded border"
                      >
                        <div className="flex items-center gap-2">
                          {result.success ? (
                            <CheckCircle className="h-4 w-4 text-green-500" />
                          ) : (
                            <XCircle className="h-4 w-4 text-red-500" />
                          )}
                          <span className="text-sm font-medium">
                            {result.operation}
                          </span>
                        </div>

                        <div className="flex items-center gap-2">
                          <Badge variant="outline" className="text-xs">
                            {result.duration}ms
                          </Badge>
                          {result.error && (
                            <Badge variant="destructive" className="text-xs">
                              Error
                            </Badge>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>

                  {suite.results.some(r => r.error) && (
                    <div className="mt-4 p-3 bg-red-50 border border-red-200 rounded">
                      <h4 className="text-sm font-semibold text-red-800 mb-2">Errors:</h4>
                      {suite.results
                        .filter(r => r.error)
                        .map((result, errorIndex) => (
                          <div key={errorIndex} className="text-sm text-red-700">
                            <strong>{result.operation}:</strong> {result.error}
                          </div>
                        ))}
                    </div>
                  )}
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';

interface UseFormWithZodOptions<T> {
  schema: z.ZodSchema<T>;
  defaultValues: T;
}

export function useFormWithZod<T>({ schema, defaultValues }: UseFormWithZodOptions<T>) {
  return useForm<T>({
    resolver: zodResolver(schema),
    defaultValues: defaultValues as any,
  });
}

import { render, screen, fireEvent } from '@testing-library/react';
import { describe, it, expect, vi, beforeEach } from 'vitest';
import { Home, Settings, Calendar } from 'lucide-react';
import { SimpleNavbar } from '../simple-navbar';

describe('SimpleNavbar', () => {
  const mockItems = [
    { name: 'Home', value: 'home', icon: Home },
    { name: 'Settings', value: 'settings', icon: Settings },
    { name: 'Calendar', value: 'calendar', icon: Calendar },
  ];

  const mockOnItemChange = vi.fn();

  beforeEach(() => {
    vi.clearAllMocks();
  });

  it('renders all navigation items', () => {
    render(
      <SimpleNavbar
        items={mockItems}
        activeItem="home"
        onItemChange={mockOnItemChange}
      />
    );

    expect(screen.getAllByRole('button')).toHaveLength(3);
  });

  it('highlights active item', () => {
    render(
      <SimpleNavbar
        items={mockItems}
        activeItem="settings"
        onItemChange={mockOnItemChange}
      />
    );

    const buttons = screen.getAllByRole('button');
    const settingsButton = buttons[1]; // Second button is settings

    expect(settingsButton).toHaveClass('text-white');
  });

  it('calls onItemChange when item is clicked', () => {
    render(
      <SimpleNavbar
        items={mockItems}
        activeItem="home"
        onItemChange={mockOnItemChange}
      />
    );

    const buttons = screen.getAllByRole('button');
    fireEvent.click(buttons[1]); // Click settings button

    expect(mockOnItemChange).toHaveBeenCalledWith('settings');
  });

  it('applies custom className', () => {
    const { container } = render(
      <SimpleNavbar
        items={mockItems}
        activeItem="home"
        onItemChange={mockOnItemChange}
        className="custom-navbar"
      />
    );

    expect(container.firstChild).toHaveClass('custom-navbar');
  });

  it('renders icons for each item', () => {
    const { container } = render(
      <SimpleNavbar
        items={mockItems}
        activeItem="home"
        onItemChange={mockOnItemChange}
      />
    );

    // Check that SVG icons are rendered
    const svgs = container.querySelectorAll('svg');
    expect(svgs).toHaveLength(mockItems.length);
  });
});

import * as React from 'react';
import { Slot } from '@radix-ui/react-slot';
import { type VariantProps } from 'class-variance-authority';

import { cn } from '@/lib/utils';
import { buttonVariants } from '@/lib/button-variants';

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean;
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : 'button';

    return (
      <Comp
        className={cn(
          buttonVariants({ variant, size, className }),
          'shadow-sm transition-all hover:shadow-md'
        )}
        ref={ref}
        {...props}
      />
    );
  }
);
Button.displayName = 'Button';

export { Button, buttonVariants };

import React, { useRef, useEffect, useState } from 'react';
import { useSpring, animated } from '@react-spring/web';
import { LucideIcon } from 'lucide-react';
import { cn } from '@/lib/utils';
import {
  computeNavbarColors,
  setupThemeObserver,
} from '@/lib/utils/navbarColors';
import {
  calculateActiveButtonBounds,
  setButtonRef,
} from '@/lib/utils/navbarGeometry';

interface NavItem {
  name: string;
  value: string;
  icon: LucideIcon;
}

interface SimpleNavbarProps {
  items: NavItem[];
  activeItem: string;
  onItemChange: (value: string) => void;
  className?: string;
}

export function SimpleNavbar({
  items,
  activeItem,
  onItemChange,
  className,
}: SimpleNavbarProps) {
  const containerRef = useRef<HTMLDivElement>(null);
  const buttonRefs = useRef<(HTMLButtonElement | null)[]>([]);
  const [activeButtonBounds, setActiveButtonBounds] = useState({
    x: 0,
    width: 0,
  });
  const [isInitialized, setIsInitialized] = useState(false);
  const [computedColors, setComputedColors] = useState(() =>
    computeNavbarColors()
  );

  // Get computed CSS variable values and detect theme
  useEffect(() => {
    const updateColors = () => {
      setComputedColors(computeNavbarColors());
    };

    // Get colors on mount and when theme changes
    updateColors();

    // Listen for theme changes
    const cleanup = setupThemeObserver(updateColors);

    return cleanup;
  }, []);

  // Calculate active button position with proper timing
  const updateActiveButtonBounds = () => {
    const activeIndex = items.findIndex((item) => item.value === activeItem);
    const container = containerRef.current;

    if (activeIndex >= 0 && container) {
      const containerPadding = 8;
      const newBounds = calculateActiveButtonBounds(
        activeIndex,
        buttonRefs.current,
        container,
        containerPadding
      );

      setActiveButtonBounds(newBounds);

      if (!isInitialized) {
        setIsInitialized(true);
      }
    }
  };

  // Initialize and update on changes
  useEffect(() => {
    // Use requestAnimationFrame to ensure DOM is ready
    const frame = requestAnimationFrame(() => {
      updateActiveButtonBounds();
    });

    return () => {
      cancelAnimationFrame(frame);
    };
  }, [activeItem, items, computedColors]);

  // Handle window resize
  useEffect(() => {
    const handleResize = () => {
      updateActiveButtonBounds();
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [activeItem]);

  // Spring animation for active indicator line
  const indicatorLineSpring = useSpring({
    transform: `translateX(${activeButtonBounds.x}px)`,
    width: 40,
    opacity: isInitialized ? 1 : 0,
    config: {
      tension: 300,
      friction: 30,
    },
  });

  // Spring animation for button background
  const buttonBackgroundSpring = useSpring({
    transform: `translateX(${activeButtonBounds.x}px)`,
    width: activeButtonBounds.width,
    opacity: isInitialized ? 1 : 0,
    config: {
      tension: 300,
      friction: 30,
    },
  });

  // Spring animation for glow effect
  const glowSpring = useSpring({
    transform: `translateX(${activeButtonBounds.x - 8}px)`,
    width: activeButtonBounds.width + 16,
    opacity: isInitialized ? 1 : 0,
    config: {
      tension: 250,
      friction: 35,
    },
  });

  return (
    <div className={cn('flex w-full justify-center', className)}>
      <div
        ref={containerRef}
        className="relative flex items-center gap-1 rounded-full border border-gray-300 bg-gray-50/80 p-2 shadow-lg backdrop-blur-md dark:border-gray-600 dark:bg-gray-900/90"
      >
        {/* Active indicator line above button */}
        <animated.div
          style={{
            position: 'absolute',
            top: '-4px',
            left: '12px',
            height: '4px',
            borderRadius: '9999px',
            zIndex: 20,
            filter: 'blur(1px)',
            backgroundColor: computedColors.indicatorColor,
            boxShadow: computedColors.isDarkMode
              ? '0 0 8px rgba(255, 255, 255, 0.4)'
              : `0 0 8px ${computedColors.primaryGlow}`,
            ...indicatorLineSpring,
          }}
        />

        {/* Button background highlight */}
        <animated.div
          style={{
            position: 'absolute',
            left: '8px',
            height: '38px',
            borderRadius: '9999px',
            zIndex: 5,
            backgroundColor: computedColors.highlightColor,
            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
            ...buttonBackgroundSpring,
          }}
        />

        {/* Ambient glow effect */}
        <animated.div
          style={{
            position: 'absolute',
            top: '0px',
            left: '0px',
            height: '48px',
            borderRadius: '9999px',
            zIndex: 0,
            backgroundColor: computedColors.primaryVeryLight,
            filter: 'blur(12px)',
            boxShadow: `0 0 20px ${computedColors.primaryGlow}`,
            ...glowSpring,
          }}
        />

        {items.map((item, index) => {
          const Icon = item.icon;
          const isActive = activeItem === item.value;

          return (
            <button
              key={item.value}
              ref={(el) => setButtonRef(buttonRefs, index, el)}
              onClick={() => onItemChange(item.value)}
              className={cn(
                'relative z-10 min-w-[48px] cursor-pointer rounded-full border-none bg-transparent px-4 py-2 text-sm font-medium transition-all duration-200',
                isActive
                  ? 'text-white dark:text-white'
                  : 'text-gray-600 hover:text-gray-900 dark:text-gray-400 dark:hover:text-gray-200'
              )}
            >
              <span className="relative z-20 flex items-center justify-center">
                <Icon size={18} strokeWidth={2.5} />
              </span>
            </button>
          );
        })}
      </div>
    </div>
  );
}

