import {
  createContext,
  useContext,
  useState,
  useEffect,
  useMemo,
  ReactNode,
} from 'react';
import { TaskFilter } from '../types';

// Define the shape of our UI context
interface TaskUIContextType {
  // UI filters
  filter: TaskFilter;
  setFilter: (filter: TaskFilter) => void;

  // Expanded state
  expandedTaskId: string | null;
  setExpandedTaskId: (id: string | null) => void;

  // Mobile detection
  isMobile: boolean;
}

const TaskUIContext = createContext<TaskUIContextType | undefined>(undefined);

/**
 * Provider component for task UI-related state
 *
 * Manages UI-only concerns like filters, expanded state, and mobile detection
 *
 * @param children - React components that will consume the context
 */
export function TaskUIContextProvider({ children }: { children: ReactNode }) {
  // UI States
  const [filter, setFilter] = useState<TaskFilter>('all');
  const [expandedTaskId, setExpandedTaskId] = useState<string | null>(null);
  const [isMobile, setIsMobile] = useState(false);

  // Check if the device is mobile on component mount and window resize
  useEffect(() => {
    const checkIfMobile = () => {
      setIsMobile(window.innerWidth < 768);
    };

    checkIfMobile();
    window.addEventListener('resize', checkIfMobile);

    return () => window.removeEventListener('resize', checkIfMobile);
  }, []);

  const value = useMemo(
    () => ({
      filter,
      setFilter,
      expandedTaskId,
      setExpandedTaskId,
      isMobile,
    }),
    [filter, expandedTaskId, isMobile]
  );

  return (
    <TaskUIContext.Provider value={value}>{children}</TaskUIContext.Provider>
  );
}

/**
 * Custom hook for using the task UI context
 *
 * @returns The task UI context value
 * @throws Error if used outside of a TaskUIContextProvider
 */
export function useTaskUIContext() {
  const context = useContext(TaskUIContext);
  if (context === undefined) {
    throw new Error(
      'useTaskUIContext must be used within a TaskUIContextProvider'
    );
  }
  return context;
}

import { useRef, useCallback, useMemo } from 'react';
import { Task } from '@/types';
import { useTaskUIContext } from '@/features/tasks/context/TaskUIContext';
import { useTaskAnimation } from '@/features/tasks/hooks/useTaskAnimation';
import { useTaskMutations } from './useTaskMutations';
import { toast } from '@/lib/toast';

/**
 * Custom hook for TaskCard functionality
 *
 * Provides task card state management, expansion handling, and pin toggling functionality
 *
 * @param task - The task data to display in the card
 * @returns Object containing refs, state, and handlers for the TaskCard component
 */
export function useTaskCard(task: Task) {
  const { toggleTaskPin } = useTaskMutations();
  const { expandedTaskId, setExpandedTaskId } = useTaskUIContext();
  const contentRef = useRef<HTMLDivElement>(null);
  const cardRef = useRef<HTMLDivElement>(null);

  // Memoize expansion state to prevent unnecessary recalculations
  const isExpanded = useMemo(
    () => expandedTaskId === task.id,
    [expandedTaskId, task.id]
  );

  // Custom hook for task animations
  const { animationState } = useTaskAnimation(contentRef, isExpanded);

  /**
   * Toggle the expanded state of the task card
   * Optimized: Only recreate when necessary dependencies change
   */
  const toggleExpand = useCallback(() => {
    setExpandedTaskId(isExpanded ? null : task.id);
  }, [setExpandedTaskId, isExpanded, task.id]);

  /**
   * Toggle the pinned state of the task
   * Optimized: Only recreate when task.id or task.pinned changes
   */
  const handleTogglePin = useCallback(async () => {
    const result = await toggleTaskPin(task);
    if (result.success) {
      toast.success(result.message);
    } else if (result.error) {
      toast.error(result.message);
    }
  }, [toggleTaskPin, task.id, task.pinned]);

  // Memoize the return object to prevent unnecessary re-renders
  return useMemo(
    () => ({
      contentRef,
      cardRef,
      isExpanded,
      animationState,
      toggleExpand,
      handleTogglePin,
    }),
    [isExpanded, animationState, toggleExpand, handleTogglePin]
  );
}

import { useCallback } from 'react';
import { useObjectValidation, useFieldValidation } from '@/hooks/useValidation';
import { createTaskSchema } from '@/features/tasks/schemas/taskSchema';
import { createTextSchema } from '@/schemas/commonValidation';

/**
 * Standardized task form validation hook
 *
 * Provides consistent validation patterns for all task forms using Zod schemas
 */
export function useTaskFormValidation() {
  const { validateWithError } = useObjectValidation();
  const { validateField } = useFieldValidation();

  /**
   * Validate complete task form data using the task schema
   */
  const validateTaskForm = useCallback(
    (data: any) => {
      return validateWithError(createTaskSchema, data);
    },
    [validateWithError]
  );

  /**
   * Validate title with character limit
   */
  const validateTitle = useCallback(
    (value: string): boolean => {
      const titleSchema = createTextSchema(1, 22, true);
      const result = validateField(titleSchema, value, 'title');
      return result.isValid;
    },
    [validateField]
  );

  /**
   * Create a title setter with validation and character limit enforcement
   */
  const createTitleSetter = useCallback((setTitle: (value: string) => void) => {
    return (value: string) => {
      if (value.length <= 22) {
        setTitle(value);
      }
    };
  }, []);

  return {
    validateTaskForm,
    validateTitle,
    createTitleSetter,
  };
}

import { useCallback } from 'react';
import { useQueryClient } from '@tanstack/react-query';
import { Task } from '@/types';
import {
  updateTaskStatus,
  toggleTaskPin as apiToggleTaskPin,
  createFollowUpTask as apiCreateFollowUpTask,
} from '@/integrations/supabase/api/tasks.api';

/**
 * Consolidated hook for all task mutation operations
 *
 * Single source of truth for task mutations with consistent error handling,
 * optimistic updates, and user feedback.
 *
 * UI side effects (toasts, notifications) are now handled by the UI layer.
 */
export function useTaskMutations() {
  const queryClient = useQueryClient();

  /**
   * Optimistically update a task in the cache
   */
  const updateTaskOptimistically = useCallback(
    (taskId: string, updates: Record<string, any>, fallbackData?: any) => {
      queryClient.setQueriesData({ queryKey: ['tasks'] }, (oldData: any) => {
        if (!oldData) return fallbackData || oldData;

        if (oldData.pages) {
          return {
            ...oldData,
            pages: oldData.pages.map((page: any) => ({
              ...page,
              data: page.data.map((t: any) =>
                t.id === taskId ? { ...t, ...updates } : t
              ),
            })),
          };
        }

        const newData = oldData.data
          ? {
              ...oldData,
              data: {
                ...oldData.data,
                data: oldData.data.data.map((t: any) =>
                  t.id === taskId ? { ...t, ...updates } : t
                ),
              },
            }
          : oldData;

        return newData;
      });
    },
    [queryClient]
  );

  /**
   * Toggle task completion status
   */
  const toggleTaskComplete = useCallback(
    async (task: Task) => {
      const newStatus = task.status === 'complete' ? 'pending' : 'complete';
      const previousData = queryClient.getQueryData([
        'tasks',
        undefined,
        undefined,
      ]);

      try {
        updateTaskOptimistically(task.id, { status: newStatus });

        const { error } = await updateTaskStatus(task.id, newStatus);
        if (error) throw error;

        // UI layer should handle haptic feedback, toast, and browser notification
        return {
          success: true,
          error: null,
          message: `Task marked ${newStatus}`,
          status: newStatus,
          task,
        };
      } catch (error: any) {
        updateTaskOptimistically(
          task.id,
          { status: task.status },
          previousData
        );

        return {
          success: false,
          error: error instanceof Error ? error.message : 'Failed to update task',
          message: `Failed to update task: ${error instanceof Error ? error.message : ''}`,
          status: task.status,
          task,
        };
      }
    },
    [updateTaskOptimistically, queryClient]
  );

  /**
   * Toggle task pin status
   */
  const toggleTaskPin = useCallback(
    async (task: Task) => {
      const newPinnedState = !task.pinned;
      const previousData = queryClient.getQueryData([
        'tasks',
        undefined,
        undefined,
      ]);

      try {
        updateTaskOptimistically(task.id, { pinned: newPinnedState });

        const { error } = await apiToggleTaskPin(task.id, newPinnedState);
        if (error) throw error;

        return {
          success: true,
          error: null,
          message: `Task ${newPinnedState ? 'pinned' : 'unpinned'}`,
          pinned: newPinnedState,
          task,
        };
      } catch (error: any) {
        updateTaskOptimistically(
          task.id,
          { pinned: task.pinned },
          previousData
        );

        return {
          success: false,
          error: error instanceof Error ? error.message : `Failed to ${newPinnedState ? 'pin' : 'unpin'} task`,
          message: `Failed to ${newPinnedState ? 'pin' : 'unpin'} task: ${error instanceof Error ? error.message : ''}`,
          pinned: task.pinned,
          task,
        };
      }
    },
    [updateTaskOptimistically, queryClient]
  );

  /**
   * Create a follow-up task
   */
  const createFollowUpTask = useCallback(
    async (parentTask: Task, taskData: any) => {
      try {
        const { error } = await apiCreateFollowUpTask(parentTask.id, taskData);
        if (error) throw error;

        queryClient.invalidateQueries({ queryKey: ['tasks'] });
        return {
          success: true,
          error: null,
          message: 'Follow-up task created successfully',
          parentTask,
          taskData,
        };
      } catch (error: any) {
        return {
          success: false,
          error: error instanceof Error ? error.message : 'Failed to create follow-up task',
          message: `Failed to create follow-up task: ${error instanceof Error ? error.message : ''}`,
          parentTask,
          taskData,
        };
      }
    },
    [queryClient]
  );

  return {
    toggleTaskComplete,
    toggleTaskPin,
    createFollowUpTask,
  };
}

import { useCallback } from 'react';
import { useTaskFormValidation } from './useTaskFormValidation';

/**
 * Task form validation hook
 *
 * Provides validation functions specifically for task forms
