import { toast } from '@/hooks/use-toast';
import { ApiError } from '@/integrations/supabase/types/api.types';

/**
 * Standardized error handling utilities for the application.
 *
 * These utilities provide consistent error handling patterns
 * across different parts of the application.
 */

/**
 * Configuration for error handling
 */
export interface ErrorHandlingOptions {
  showToast?: boolean;
  logToConsole?: boolean;
  rethrow?: boolean;
}

const defaultOptions: ErrorHandlingOptions = {
  showToast: true,
  logToConsole: true,
  rethrow: false,
};

/**
 * Process API errors in a standardized way
 *
 * @param error - The error to process
 * @param customMessage - Optional custom message to display instead of the error message
 * @param options - Error handling configuration options
 */
export const handleApiError = (
  error: Error | ApiError | unknown,
  customMessage?: string,
  options: ErrorHandlingOptions = {}
): Error => {
  // Merge with default options
  const opts = { ...defaultOptions, ...options };

  let errorMessage = customMessage || 'An unexpected error occurred';
  let errorObject: Error;

  // Process different error types
  if (error instanceof Error) {
    errorMessage = customMessage || error.message;
    errorObject = error;
  } else if (
    typeof error === 'object' &&
    error !== null &&
    'message' in error &&
    typeof error.message === 'string'
  ) {
    // Handle API error objects
    errorMessage = customMessage || error.message;
    errorObject = new Error(errorMessage);

    // Copy properties from ApiError
    if ('code' in error) {
      (errorObject as any).code = error.code;
    }
    if ('details' in error) {
      (errorObject as any).details = error.details;
    }
  } else {
    // Handle unexpected error types
    errorObject = new Error(errorMessage);
    (errorObject as any).originalError = error;
  }

  // Display error toast if enabled
  if (opts.showToast) {
    toast({
      title: 'Error',
      description: errorMessage,
      variant: 'destructive',
    });
  }

  // Log error to console if enabled
  if (opts.logToConsole) {
    console.error('[Error]:', errorMessage, errorObject);
  }

  // Rethrow if required
  if (opts.rethrow) {
    throw errorObject;
  }

  return errorObject;
};

/**
 * Utility to safely execute async functions with standardized error handling
 *
 * @param asyncFn - The async function to execute
 * @param errorMessage - Optional custom error message
 * @param options - Error handling options
 * @returns Promise resolving to the function result or null if there was an error
 */
export async function safeAsync<T>(
  asyncFn: () => Promise<T>,
  errorMessage?: string,
  options?: ErrorHandlingOptions
): Promise<T | null> {
  try {
    return await asyncFn();
  } catch (error) {
    handleApiError(error, errorMessage, options);
    return null;
  }
}

/**
 * Higher-order function that wraps an async function with error handling
 *
 * @param fn - The async function to wrap
 * @param errorMessage - Optional custom error message
 * @param options - Error handling options
 * @returns A wrapped function with error handling
 */
export function withErrorHandling<T extends (...args: any[]) => Promise<any>>(
  fn: T,
  errorMessage?: string,
  options?: ErrorHandlingOptions
): (...args: Parameters<T>) => Promise<ReturnType<T> | null> {
  return async (...args: Parameters<T>): Promise<ReturnType<T> | null> => {
    try {
      return await fn(...args);
    } catch (error) {
      handleApiError(error, errorMessage, options);
      return null;
    }
  };
}

/**
 * Core performance monitoring utilities (React-agnostic)
 */

interface PerformanceMetrics {
  name: string;
  startTime: number;
  endTime?: number;
  duration?: number;
  fps?: number;
  memoryUsage?: number;
}

class PerformanceMonitor {
  private metrics: PerformanceMetrics[] = [];
  private frameCount = 0;
  private lastFPSUpdate = 0;
  private currentFPS = 0;

  /**
   * Start measuring performance for a given operation
   */
  startMeasurement(name: string): string {
    const id = `${name}-${Date.now()}`;
    const metric: PerformanceMetrics = {
      name: id,
      startTime: performance.now(),
    };

    this.metrics.push(metric);

    if (process.env.NODE_ENV === 'development') {
      console.log(`🚀 Performance measurement started: ${name}`);
    }

    return id;
  }

  /**
   * End measurement and calculate duration
   */
  endMeasurement(id: string): number | null {
    const metric = this.metrics.find((m) => m.name === id);
    if (!metric) return null;

    metric.endTime = performance.now();
    metric.duration = metric.endTime - metric.startTime;

    // Add memory usage if available
    if ('memory' in performance) {
      metric.memoryUsage = (performance as any).memory.usedJSHeapSize;
    }

    if (process.env.NODE_ENV === 'development') {
      console.log(
        `⏱️ Performance measurement completed: ${id} - ${metric.duration.toFixed(2)}ms`
      );

      if (metric.duration > 16.67) {
        // More than one frame at 60fps
        console.warn(
          `⚠️ Slow operation detected: ${id} took ${metric.duration.toFixed(2)}ms`
        );
      }
    }

    return metric.duration;
  }

  /**
   * Monitor FPS during animations
   */
  startFPSMonitoring(): () => void {
    let animationId: number;
    this.frameCount = 0;
    this.lastFPSUpdate = performance.now();

    const updateFPS = () => {
      this.frameCount++;
      const now = performance.now();

      if (now - this.lastFPSUpdate >= 1000) {
        // Update every second
        this.currentFPS = Math.round(
          (this.frameCount * 1000) / (now - this.lastFPSUpdate)
        );

        if (process.env.NODE_ENV === 'development') {
          console.log(`📊 Current FPS: ${this.currentFPS}`);

          if (this.currentFPS < 30) {
            console.warn(`⚠️ Low FPS detected: ${this.currentFPS}`);
          }
        }

        this.frameCount = 0;
        this.lastFPSUpdate = now;
      }

      animationId = requestAnimationFrame(updateFPS);
    };

    animationId = requestAnimationFrame(updateFPS);

    // Return cleanup function
    return () => {
      cancelAnimationFrame(animationId);
    };
  }

  /**
   * Get performance report
   */
  getReport(): PerformanceMetrics[] {
    return this.metrics.filter((m) => m.duration !== undefined);
  }

  /**
   * Clear all metrics
   */
  clearMetrics(): void {
    this.metrics = [];
  }

  /**
   * Get current FPS
   */
  getCurrentFPS(): number {
    return this.currentFPS;
  }
}

// Export singleton instance
export const performanceMonitor = new PerformanceMonitor();

