import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { runAllCRUDTests } from '@/lib/testing/crudTesting';
import { Loader2, Play, CheckCircle, XCircle, Clock } from 'lucide-react';

interface TestSuite {
  suiteName: string;
  results: any[];
  totalDuration: number;
  passed: number;
  failed: number;
}

export default function DatabaseTestPanel() {
  const [isRunning, setIsRunning] = useState(false);
  const [testResults, setTestResults] = useState<TestSuite[]>([]);
  const [lastRun, setLastRun] = useState<Date | null>(null);

  const handleRunTests = async () => {
    setIsRunning(true);
    try {
      const results = await runAllCRUDTests();
      setTestResults(results);
      setLastRun(new Date());
    } catch (error) {
      console.error('Test execution failed:', error);
    } finally {
      setIsRunning(false);
    }
  };

  const getTotalStats = () => {
    const totalPassed = testResults.reduce((sum, suite) => sum + suite.passed, 0);
    const totalFailed = testResults.reduce((sum, suite) => sum + suite.failed, 0);
    const totalDuration = testResults.reduce((sum, suite) => sum + suite.totalDuration, 0);

    return { totalPassed, totalFailed, totalDuration };
  };

  const { totalPassed, totalFailed, totalDuration } = getTotalStats();

  return (
    <Card className="w-full max-w-4xl mx-auto">
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <CheckCircle className="h-5 w-5" />
          Database Integrity Testing
        </CardTitle>
        <CardDescription>
          Test all CRUD operations and validate data integrity constraints
        </CardDescription>
      </CardHeader>

      <CardContent className="space-y-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-4">
            <Button 
              onClick={handleRunTests} 
              disabled={isRunning}
              className="flex items-center gap-2"
            >
              {isRunning ? (
                <Loader2 className="h-4 w-4 animate-spin" />
              ) : (
                <Play className="h-4 w-4" />
              )}
              {isRunning ? 'Running Tests...' : 'Run All Tests'}
            </Button>

            {lastRun && (
              <div className="flex items-center gap-2 text-sm text-muted-foreground">
                <Clock className="h-4 w-4" />
                Last run: {lastRun.toLocaleTimeString()}
              </div>
            )}
          </div>

          {testResults.length > 0 && (
            <div className="flex items-center gap-2">
              <Badge variant={totalFailed === 0 ? "default" : "destructive"}>
                {totalPassed}/{totalPassed + totalFailed} Passed
              </Badge>
              <Badge variant="outline">
                {totalDuration}ms
              </Badge>
            </div>
          )}
        </div>

        {testResults.length > 0 && (
          <div className="space-y-4">
            <h3 className="text-lg font-semibold">Test Results</h3>

            {testResults.map((suite, index) => (
              <Card key={index} className="border-l-4 border-l-primary">
                <CardHeader className="pb-3">
                  <div className="flex items-center justify-between">
                    <CardTitle className="text-base">{suite.suiteName}</CardTitle>
                    <div className="flex items-center gap-2">
                      <Badge variant={suite.failed === 0 ? "default" : "destructive"}>
                        {suite.passed}/{suite.passed + suite.failed}
                      </Badge>
                      <Badge variant="outline">
                        {suite.totalDuration}ms
                      </Badge>
                    </div>
                  </div>
                </CardHeader>

                <CardContent>
                  <div className="space-y-2">
                    {suite.results.map((result, resultIndex) => (
                      <div 
                        key={resultIndex}
                        className="flex items-center justify-between p-2 rounded border"
                      >
                        <div className="flex items-center gap-2">
                          {result.success ? (
                            <CheckCircle className="h-4 w-4 text-green-500" />
                          ) : (
                            <XCircle className="h-4 w-4 text-red-500" />
                          )}
                          <span className="text-sm font-medium">
                            {result.operation}
                          </span>
                        </div>

                        <div className="flex items-center gap-2">
                          <Badge variant="outline" className="text-xs">
                            {result.duration}ms
                          </Badge>
                          {result.error && (
                            <Badge variant="destructive" className="text-xs">
                              Error
                            </Badge>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>

                  {suite.results.some(r => r.error) && (
                    <div className="mt-4 p-3 bg-red-50 border border-red-200 rounded">
                      <h4 className="text-sm font-semibold text-red-800 mb-2">Errors:</h4>
                      {suite.results
                        .filter(r => r.error)
                        .map((result, errorIndex) => (
                          <div key={errorIndex} className="text-sm text-red-700">
                            <strong>{result.operation}:</strong> {result.error}
                          </div>
                        ))}
                    </div>
                  )}
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

import { Input } from '@/components/ui/input';
import { useState, ReactNode } from 'react';
import { cn } from '@/lib/utils';
import { AnimatedCharacterCount } from './AnimatedCharacterCount';

interface FloatingInputProps {
  id: string;
  type?: string;
  value: string;
  onChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  placeholder: string;
  label: string;
  icon?: ReactNode;
  maxLength?: number;
  required?: boolean;
  autoFocus?: boolean;
  className?: string;
}

export function FloatingInput({
  id,
  type = 'text',
  value,
  onChange,
  placeholder,
  label,
  icon,
  maxLength,
  required = false,
  autoFocus = false,
  className,
}: FloatingInputProps) {
  const [isFocused, setIsFocused] = useState(false);
  const hasValue = value.length > 0;
  const isFloating = isFocused || hasValue;
  const showCounter = maxLength && (isFocused || hasValue);

  return (
    <div className={cn('group relative', className)}>
      <div className="relative">
        {icon && (
          <div
            className={cn(
              'absolute left-3 top-1/2 z-10 -translate-y-1/2 transform transition-all duration-300',
              isFloating ? 'scale-95 text-primary' : 'text-muted-foreground'
            )}
          >
            {icon}
          </div>
        )}

        <Input
          id={id}
          type={type}
          value={value}
          onChange={onChange}
          onFocus={() => setIsFocused(true)}
          onBlur={() => setIsFocused(false)}
          placeholder=""
          maxLength={maxLength}
          required={required}
          autoFocus={autoFocus}
          className={cn(
            'peer h-14 rounded-2xl border-border/40 bg-background/60 pb-2 pt-6 backdrop-blur-sm transition-all duration-300 hover:border-border/60 hover:bg-background/70 focus:border-primary/60 focus:bg-background/80 focus:shadow-lg focus:shadow-primary/10',
            icon ? 'pl-11' : 'pl-4',
            maxLength ? 'pr-16' : 'pr-4'
          )}
        />

        <label
          htmlFor={id}
          className={cn(
            'pointer-events-none absolute select-none font-medium transition-all duration-300',
            icon ? 'left-11' : 'left-4',
            isFloating
              ? 'top-2 text-xs text-primary'
              : 'top-1/2 -translate-y-1/2 text-sm text-muted-foreground'
          )}
        >
          {label}
        </label>

        {/* Character counter inside input field */}
        {showCounter && (
          <div className="absolute right-3 top-1/2 z-10 -translate-y-1/2 transform">
            <AnimatedCharacterCount current={value.length} max={maxLength} />
          </div>
        )}
      </div>

      {/* Enhanced focus ring */}
      <div
        className={cn(
          'pointer-events-none absolute inset-0 rounded-2xl transition-all duration-300',
          isFocused &&
            'ring-2 ring-primary/30 ring-offset-2 ring-offset-background'
        )}
      />
    </div>
  );
}

import { Textarea } from '@/components/ui/textarea';
import { useState, useRef, useEffect, ReactNode } from 'react';
import { cn } from '@/lib/utils';

interface FloatingTextareaProps {
  id: string;
  value: string;
  onChange: (e: React.ChangeEvent<HTMLTextAreaElement>) => void;
  placeholder: string;
  label: string;
  icon?: ReactNode;
  className?: string;
}

export function FloatingTextarea({
  id,
  value,
  onChange,
  placeholder,
  label,
  icon,
  className,
}: FloatingTextareaProps) {
  const [isFocused, setIsFocused] = useState(false);
  const textareaRef = useRef<HTMLTextAreaElement>(null);
  const hasValue = value.length > 0;
  const isFloating = isFocused || hasValue;

  // Auto-expand textarea with doubled minimum height
  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto';
      textareaRef.current.style.height = `${Math.max(112, textareaRef.current.scrollHeight)}px`; // Doubled from 56 to 112
    }
  }, [value]);

  return (
    <div className={cn('group relative', className)}>
      <div className="relative">
        {icon && (
          <div
            className={cn(
              'absolute left-3 top-3 z-10 transition-all duration-300', // Changed from top-6 to top-3
              isFloating ? 'scale-95 text-primary' : 'text-muted-foreground'
            )}
          >
            {icon}
          </div>
        )}

        <Textarea
          ref={textareaRef}
          id={id}
          value={value}
          onChange={onChange}
          onFocus={() => setIsFocused(true)}
          onBlur={() => setIsFocused(false)}
          placeholder=""
          rows={1}
          className={cn(
            'peer min-h-28 resize-none overflow-hidden rounded-2xl border-border/40 bg-background/60 pb-2 pt-6 backdrop-blur-sm transition-all duration-300 hover:border-border/60 hover:bg-background/70 focus:border-primary/60 focus:bg-background/80 focus:shadow-lg focus:shadow-primary/10', // Changed min-h-14 to min-h-28 (doubled)
            icon ? 'pl-11' : 'pl-4',
            'pr-4'
          )}
        />

        <label
          htmlFor={id}
          className={cn(
            'pointer-events-none absolute select-none font-medium transition-all duration-300',
            icon ? 'left-11' : 'left-4',
            isFloating
              ? 'top-2 text-xs text-primary' // Keeps the floated label at top-2
              : 'top-3 text-sm text-muted-foreground' // Changed from top-6 to top-3 for better alignment
          )}
        >
          {label}
        </label>
      </div>

      {/* Elegant focus ring with animation */}
      <div
        className={cn(
          'pointer-events-none absolute inset-0 rounded-2xl transition-all duration-300',
          isFocused &&
            'ring-2 ring-primary/30 ring-offset-2 ring-offset-background'
        )}
      />
    </div>
  );
}

import { useState } from 'react';
import { Link } from 'lucide-react';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { FloatingInput } from '@/components/form/FloatingInput';
import { Button } from '@/components/ui/button';
import { cn } from '@/lib/utils';

interface UrlInputModalProps {
  isOpen: boolean;
  onClose: () => void;
  value: string;
  onChange: (value: string) => void;
}

export function UrlInputModal({
  isOpen,
  onClose,
  value,
  onChange,
}: UrlInputModalProps) {
  const [tempValue, setTempValue] = useState(value);
  const [error, setError] = useState<string | null>(null);

  const validateUrl = (url: string): boolean => {
    // Simple URL validation regex
    const urlPattern = /^(https?:\/\/)?([\w-]+(\.[\w-]+)+)(\/[\w-./?%&=]*)?$/i;
    return urlPattern.test(url);
  };

  const handleSave = () => {
    if (!tempValue) {
      // Allow empty URL (optional field)
      onChange(tempValue);
      onClose();
      return;
    }

    if (!validateUrl(tempValue)) {
      setError('Please enter a valid URL (e.g., https://example.com)');
      return;
    }

    setError(null);
    onChange(tempValue);
    onClose();
  };

  const handleCancel = () => {
    setTempValue(value);
    setError(null);
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Link className="h-5 w-5" />
            Add Reference URL
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          <div>
            <FloatingInput
              id="url-input"
              type="text"
              value={tempValue}
              onChange={(e) => {
                setTempValue(e.target.value);
                setError(null); // Clear error on input change
              }}
              placeholder="https://example.com"
              label="URL"
              icon={<Link className="h-4 w-4" />}
              autoFocus
            />
            {error && (
              <p
                className={cn(
                  'mt-1 text-sm text-destructive',
                  'animate-in fade-in-0'
                )}
              >
                {error}
              </p>
            )}
          </div>

          <div className="flex justify-end">
            <Button onClick={handleSave}>Save URL</Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

import { useState } from 'react';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { EnhancedUserSearch } from '@/components/form/EnhancedUserSearch';
import { VisuallyHidden } from '@radix-ui/react-visually-hidden';

interface UserSearchModalProps {
  isOpen: boolean;
  onClose: () => void;
  value: string;
  onChange: (value: string) => void;
}

export function UserSearchModal({
  isOpen,
  onClose,
  value,
  onChange,
}: UserSearchModalProps) {
  const [tempValue, setTempValue] = useState(value);

  const handleUserSelect = (selectedValue: string) => {
    onChange(selectedValue);
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="pb-8 sm:max-w-md">
        <DialogHeader>
          <VisuallyHidden asChild>
            <DialogTitle>Assign Task</DialogTitle>
          </VisuallyHidden>
        </DialogHeader>

        <div className="space-y-4 pb-4">
          <EnhancedUserSearch value={tempValue} onChange={handleUserSelect} />
        </div>
      </DialogContent>
    </Dialog>
  );
}

import { useFormWithZod } from './useFormWithZod';
import { useFormSubmission } from './useFormSubmission';
import { z } from 'zod';

interface UseFormWithValidationOptions<T> {
  schema: z.ZodSchema<T>;
  defaultValues: T;
  onSubmit: (data: T) => Promise<void>;
  successMessage?: string;
}

/**
 * @deprecated Use useFormWithZod and useFormSubmission instead.
 */
export function useFormWithValidation<T>({
  schema,
  defaultValues,
  onSubmit,
  successMessage = 'Success',
}: UseFormWithValidationOptions<T>) {
  const form = useFormWithZod({ schema, defaultValues });
  const { handleSubmit, formError, formSuccess } = useFormSubmission({
    onSubmit,
    successMessage,
    reset: form.reset,
  });

  if (process.env.NODE_ENV !== 'production') {
    // eslint-disable-next-line no-console
    console.warn('useFormWithValidation is deprecated. Use useFormWithZod and useFormSubmission instead.');
  }

  return {
    ...form,
    onSubmit: handleSubmit,
    isSubmitting: form.formState.isSubmitting,
    formError,
    formSuccess,
  };
}

export { useFormWithZod };
export { useFormSubmission };

import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';

interface UseFormWithZodOptions<T> {
  schema: z.ZodSchema<T>;
  defaultValues: T;
}

export function useFormWithZod<T>({ schema, defaultValues }: UseFormWithZodOptions<T>) {
  return useForm<T>({
    resolver: zodResolver(schema),
    defaultValues: defaultValues as any,
  });
}

import { memo, useMemo } from 'react';
import { animated, SpringValue } from '@react-spring/web';
import { TaskStatus } from '@/types';
import { getTimerColor } from '@/features/tasks/utils/taskUiUtils';

/**
 * TimerRing Component
 *
 * Renders the circular progress indicator for the countdown timer with the appropriate
 * styling based on task status. Uses SVG with gradients and filters for visual effects.
 *
 * @param size - The diameter of the timer ring in pixels
 * @param radius - The radius of the timer ring in pixels
 * @param circumference - The circumference of the timer ring
 * @param strokeDashoffset - The animated stroke dash offset value
 * @param status - The current status of the task
 */
interface TimerRingProps {
  size: number;
  radius: number;
  circumference: number;
  strokeDashoffset: SpringValue<number> | number;
  status: TaskStatus;
}

// Define gradients once outside the component to avoid recreating them on each render
// This is moved to a separate component and memoized to prevent re-renders
const GradientDefs = memo(() => (
  <defs>
    <linearGradient id="gradientPending" x1="0%" y1="0%" x2="100%" y2="100%">
      <stop offset="0%" stopColor="#FFDD24" />
      <stop offset="100%" stopColor="#FFE082" />
    </linearGradient>
    <linearGradient id="gradientOverdue" x1="0%" y1="0%" x2="100%" y2="100%">
      <stop offset="0%" stopColor="#DA3E52" />
      <stop offset="100%" stopColor="#E57373" />
    </linearGradient>
    <linearGradient id="gradientComplete" x1="0%" y1="0%" x2="100%" y2="100%">
      <stop offset="0%" stopColor="#10B981" />
      <stop offset="100%" stopColor="#34D399" />
    </linearGradient>

    {/* Add filters for glow effects */}
    <filter id="glowOverdue" x="-20%" y="-20%" width="140%" height="140%">
      <feGaussianBlur stdDeviation="2" result="blur" />
      <feComposite in="SourceGraphic" in2="blur" operator="over" />
    </filter>

    <filter id="glowPending" x="-20%" y="-20%" width="140%" height="140%">
      <feGaussianBlur stdDeviation="1" result="blur" />
      <feComposite in="SourceGraphic" in2="blur" operator="over" />
    </filter>

    <filter id="glowComplete" x="-20%" y="-20%" width="140%" height="140%">
      <feGaussianBlur stdDeviation="1.5" result="blur" />
      <feComposite in="SourceGraphic" in2="blur" operator="over" />
    </filter>
  </defs>
));

GradientDefs.displayName = 'GradientDefs';

const TimerRing = ({
  size,
  radius,
  circumference,
  strokeDashoffset,
  status,
}: TimerRingProps) => {
  // Memoize derived values that don't need to be recalculated on every render
  const staticProps = useMemo(() => {
    const gradientId =
      status === 'pending'
        ? 'url(#gradientPending)'
        : status === 'overdue'
          ? 'url(#gradientOverdue)'
          : 'url(#gradientComplete)';

    const filterId =
      status === 'overdue'
        ? 'url(#glowOverdue)'
        : status === 'complete'
          ? 'url(#glowComplete)'
          : 'url(#glowPending)';

    const strokeWidth = status === 'overdue' ? '5px' : '4px';

    return { gradientId, filterId, strokeWidth };
  }, [status]);

  return (
    <svg
      width={size}
      height={size}
      viewBox={`0 0 ${size} ${size}`}
      style={{
        overflow: 'visible',
      }}
      className="timer-ring"
      aria-hidden="true" // Mark as decorative for accessibility
    >
      <GradientDefs />

      {/* Background circle */}
      <circle
        cx={size / 2}
        cy={size / 2}
        r={radius}
        fill="none"
        strokeWidth="2.5"
        stroke="#F9FAFB"
        style={{ strokeWidth: '2.5px' }}
      />

      {/* Animated foreground circle */}
      <animated.circle
        cx={size / 2}
        cy={size / 2}
        r={radius}
        fill="none"
        stroke={staticProps.gradientId}
        strokeDasharray={circumference}
        strokeDashoffset={strokeDashoffset}
        transform={`rotate(-90, ${size / 2}, ${size / 2})`}
        strokeLinecap="round"
        className="timer-progress"
        style={{
          filter: staticProps.filterId,
          strokeWidth: staticProps.strokeWidth,
        }}
      />
    </svg>
  );
};

// Use React.memo with a custom equality function to prevent unnecessary re-renders
export default memo(TimerRing, (prevProps, nextProps) => {
  return (
    prevProps.size === nextProps.size &&
    prevProps.radius === nextProps.radius &&
    prevProps.circumference === nextProps.circumference &&
    prevProps.status === nextProps.status &&
    // For the strokeDashoffset, we need special handling since it might be a SpringValue
    (prevProps.strokeDashoffset === nextProps.strokeDashoffset ||
      (typeof prevProps.strokeDashoffset === 'number' &&
        typeof nextProps.strokeDashoffset === 'number' &&
        prevProps.strokeDashoffset === nextProps.strokeDashoffset))
  );
});

import * as React from 'react';
import { Slot } from '@radix-ui/react-slot';
import { type VariantProps } from 'class-variance-authority';

import { cn } from '@/lib/utils';
import { buttonVariants } from '@/lib/button-variants';

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean;
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : 'button';

    return (
      <Comp
        className={cn(
          buttonVariants({ variant, size, className }),
          'shadow-sm transition-all hover:shadow-md'
        )}
        ref={ref}
        {...props}
      />
    );
  }
);
Button.displayName = 'Button';

export { Button, buttonVariants };

import * as React from 'react';
import { ChevronLeft, ChevronRight } from 'lucide-react';
import { DayPicker } from 'react-day-picker';

import { cn } from '@/lib/utils';
import { buttonVariants } from '@/lib/button-variants';

export type CalendarProps = React.ComponentProps<typeof DayPicker>;

function Calendar({
  className,
  classNames,
  showOutsideDays = true,
  ...props
}: CalendarProps) {
  return (
    <div style={{ backgroundColor: '#1a1a1a', color: '#ffffff' }}>
      <DayPicker
        showOutsideDays={showOutsideDays}
        className={cn('p-3', className)}
        classNames={{
          months:
            'flex flex-col sm:flex-row space-y-4 sm:space-x-4 sm:space-y-0',
          month: 'space-y-4',
          caption: 'flex justify-center pt-1 relative items-center',
          caption_label: 'text-sm font-medium',
          nav: 'space-x-1 flex items-center',
          nav_button: cn(
            buttonVariants({ variant: 'outline' }),
            'h-7 w-7 bg-transparent p-0 opacity-50 hover:opacity-100'
          ),
          nav_button_previous: 'absolute left-1',
          nav_button_next: 'absolute right-1',
          table: 'w-full border-collapse space-y-1',
          head_row: 'flex',
          head_cell: 'rounded-md w-9 font-normal text-[0.8rem]',
          row: 'flex w-full mt-2',
          cell: 'h-9 w-9 text-center text-sm p-0 relative [&:has([aria-selected].day-range-end)]:rounded-r-md [&:has([aria-selected].day-outside)]:bg-accent/50 [&:has([aria-selected])]:bg-accent first:[&:has([aria-selected])]:rounded-l-md last:[&:has([aria-selected])]:rounded-r-md focus-within:relative focus-within:z-20',
          day: cn(
            buttonVariants({ variant: 'ghost' }),
            'h-9 w-9 p-0 font-normal aria-selected:opacity-100'
          ),
          day_range_end: 'day-range-end',
          day_selected:
            'hover:text-primary-foreground focus:text-primary-foreground',
          day_today: 'text-accent-foreground',
          day_outside:
            'day-outside opacity-50 aria-selected:bg-accent/50 aria-selected:opacity-30',
          day_disabled: 'opacity-50',
          day_range_middle:
            'aria-selected:bg-accent aria-selected:text-accent-foreground',
          day_hidden: 'invisible',
          ...classNames,
        }}
        components={{
          IconLeft: ({ ..._props }) => <ChevronLeft className="h-4 w-4" />,
          IconRight: ({ ..._props }) => <ChevronRight className="h-4 w-4" />,
        }}
        style={{
          backgroundColor: '#1a1a1a',
          color: '#ffffff',
        }}
        {...props}
      />
    </div>
  );
}
Calendar.displayName = 'Calendar';

export { Calendar };

import * as React from 'react';
import * as RechartsPrimitive from 'recharts';

import { cn } from '@/lib/utils';

// Format: { THEME_NAME: CSS_SELECTOR }
const THEMES = { light: '', dark: '.dark' } as const;

export type ChartConfig = {
  [k in string]: {
    label?: React.ReactNode;
    icon?: React.ComponentType;
  } & (
    | { color?: string; theme?: never }
    | { color?: never; theme: Record<keyof typeof THEMES, string> }
  );
};

type ChartContextProps = {
  config: ChartConfig;
};

const ChartContext = React.createContext<ChartContextProps | null>(null);

function useChart() {
  const context = React.useContext(ChartContext);

  if (!context) {
    throw new Error('useChart must be used within a <ChartContainer />');
  }

  return context;
}

const ChartContainer = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<'div'> & {
    config: ChartConfig;
    children: React.ComponentProps<
      typeof RechartsPrimitive.ResponsiveContainer
    >['children'];
  }
>(({ id, className, children, config, ...props }, ref) => {
  const uniqueId = React.useId();
  const chartId = `chart-${id || uniqueId.replace(/:/g, '')}`;

  return (
    <ChartContext.Provider value={{ config }}>
      <div
        data-chart={chartId}
        ref={ref}
        className={cn(
          "flex aspect-video justify-center text-xs [&_.recharts-cartesian-axis-tick_text]:fill-muted-foreground [&_.recharts-cartesian-grid_line[stroke='#ccc']]:stroke-border/50 [&_.recharts-curve.recharts-tooltip-cursor]:stroke-border [&_.recharts-dot[stroke='#fff']]:stroke-transparent [&_.recharts-layer]:outline-none [&_.recharts-polar-grid_[stroke='#ccc']]:stroke-border [&_.recharts-radial-bar-background-sector]:fill-muted [&_.recharts-rectangle.recharts-tooltip-cursor]:fill-muted [&_.recharts-reference-line_[stroke='#ccc']]:stroke-border [&_.recharts-sector[stroke='#fff']]:stroke-transparent [&_.recharts-sector]:outline-none [&_.recharts-surface]:outline-none",
          className
        )}
        {...props}
      >
        <ChartStyle id={chartId} config={config} />
        <RechartsPrimitive.ResponsiveContainer>
          {children}
        </RechartsPrimitive.ResponsiveContainer>
      </div>
    </ChartContext.Provider>
  );
});
ChartContainer.displayName = 'Chart';

const ChartStyle = ({ id, config }: { id: string; config: ChartConfig }) => {
  const colorConfig = Object.entries(config).filter(
    ([_, config]) => config.theme || config.color
  );

  if (!colorConfig.length) {
    return null;
  }

  return (
    <style
      dangerouslySetInnerHTML={{
        __html: Object.entries(THEMES)
          .map(
            ([theme, prefix]) => `
${prefix} [data-chart=${id}] {
${colorConfig
  .map(([key, itemConfig]) => {
    const color =
      itemConfig.theme?.[theme as keyof typeof itemConfig.theme] ||
      itemConfig.color;
    return color ? `  --color-${key}: ${color};` : null;
  })
  .join('\n')}
}
`
          )
          .join('\n'),
      }}
    />
  );
};

const ChartTooltip = RechartsPrimitive.Tooltip;

const ChartTooltipContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<typeof RechartsPrimitive.Tooltip> &
    React.ComponentProps<'div'> & {
      hideLabel?: boolean;
      hideIndicator?: boolean;
      indicator?: 'line' | 'dot' | 'dashed';
      nameKey?: string;
      labelKey?: string;
    }
>(
  (
    {
      active,
      payload,
      className,
      indicator = 'dot',
      hideLabel = false,
      hideIndicator = false,
      label,
      labelFormatter,
      labelClassName,
      formatter,
      color,
      nameKey,
      labelKey,
    },
    ref
  ) => {
    const { config } = useChart();

    const tooltipLabel = React.useMemo(() => {
      if (hideLabel || !payload?.length) {
        return null;
      }

      const [item] = payload;
      const key = `${labelKey || item.dataKey || item.name || 'value'}`;
      const itemConfig = getPayloadConfigFromPayload(config, item, key);
      const value =
        !labelKey && typeof label === 'string'
          ? config[label as keyof typeof config]?.label || label
          : itemConfig?.label;

      if (labelFormatter) {
        return (
          <div className={cn('font-medium', labelClassName)}>
            {labelFormatter(value, payload)}
          </div>
        );
      }

      if (!value) {
        return null;
      }

      return <div className={cn('font-medium', labelClassName)}>{value}</div>;
    }, [
      label,
      labelFormatter,
      payload,
      hideLabel,
      labelClassName,
      config,
      labelKey,
    ]);

    if (!active || !payload?.length) {
      return null;
    }

    const nestLabel = payload.length === 1 && indicator !== 'dot';

    return (
      <div
        ref={ref}
        className={cn(
          'grid min-w-[8rem] items-start gap-1.5 rounded-lg border border-border/50 bg-background px-2.5 py-1.5 text-xs shadow-xl',
          className
        )}
      >
        {!nestLabel ? tooltipLabel : null}
        <div className="grid gap-1.5">
          {payload.map((item, index) => {
            const key = `${nameKey || item.name || item.dataKey || 'value'}`;
            const itemConfig = getPayloadConfigFromPayload(config, item, key);
            const indicatorColor = color || item.payload.fill || item.color;

            return (
              <div
                key={item.dataKey}
                className={cn(
                  'flex w-full flex-wrap items-stretch gap-2 [&>svg]:h-2.5 [&>svg]:w-2.5 [&>svg]:text-muted-foreground',
                  indicator === 'dot' && 'items-center'
                )}
              >
                {formatter && item?.value !== undefined && item.name ? (
                  formatter(item.value, item.name, item, index, item.payload)
                ) : (
                  <>
                    {itemConfig?.icon ? (
                      <itemConfig.icon />
                    ) : (
                      !hideIndicator && (
                        <div
                          className={cn(
                            'shrink-0 rounded-[2px] border-[--color-border] bg-[--color-bg]',
                            {
                              'h-2.5 w-2.5': indicator === 'dot',
                              'w-1': indicator === 'line',
                              'w-0 border-[1.5px] border-dashed bg-transparent':
                                indicator === 'dashed',
                              'my-0.5': nestLabel && indicator === 'dashed',
                            }
                          )}
                          style={
                            {
                              '--color-bg': indicatorColor,
                              '--color-border': indicatorColor,
                            } as React.CSSProperties
                          }
                        />
                      )
                    )}
                    <div
                      className={cn(
                        'flex flex-1 justify-between leading-none',
                        nestLabel ? 'items-end' : 'items-center'
                      )}
                    >
                      <div className="grid gap-1.5">
                        {nestLabel ? tooltipLabel : null}
                        <span className="text-muted-foreground">
                          {itemConfig?.label || item.name}
                        </span>
                      </div>
                      {item.value && (
                        <span className="font-mono font-medium tabular-nums text-foreground">
                          {item.value.toLocaleString()}
                        </span>
                      )}
                    </div>
                  </>
                )}
              </div>
            );
          })}
        </div>
      </div>
    );
  }
);
ChartTooltipContent.displayName = 'ChartTooltip';

const ChartLegend = RechartsPrimitive.Legend;

const ChartLegendContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<'div'> &
    Pick<RechartsPrimitive.LegendProps, 'payload' | 'verticalAlign'> & {
      hideIcon?: boolean;
      nameKey?: string;
    }
>(
  (
    { className, hideIcon = false, payload, verticalAlign = 'bottom', nameKey },
    ref
  ) => {
    const { config } = useChart();

    if (!payload?.length) {
      return null;
    }

    return (
      <div
        ref={ref}
        className={cn(
          'flex items-center justify-center gap-4',
          verticalAlign === 'top' ? 'pb-3' : 'pt-3',
          className
        )}
      >
        {payload.map((item) => {
          const key = `${nameKey || item.dataKey || 'value'}`;
          const itemConfig = getPayloadConfigFromPayload(config, item, key);

          return (
            <div
              key={item.value}
              className={cn(
                'flex items-center gap-1.5 [&>svg]:h-3 [&>svg]:w-3 [&>svg]:text-muted-foreground'
              )}
            >
              {itemConfig?.icon && !hideIcon ? (
                <itemConfig.icon />
              ) : (
                <div
                  className="h-2 w-2 shrink-0 rounded-[2px]"
                  style={{
                    backgroundColor: item.color,
                  }}
                />
              )}
              {itemConfig?.label}
            </div>
          );
        })}
      </div>
    );
  }
);
ChartLegendContent.displayName = 'ChartLegend';

// Helper to extract item config from a payload.
function getPayloadConfigFromPayload(
  config: ChartConfig,
  payload: unknown,
  key: string
) {
  if (typeof payload !== 'object' || payload === null) {
    return undefined;
  }

  const payloadPayload =
    'payload' in payload &&
    typeof payload.payload === 'object' &&
    payload.payload !== null
      ? payload.payload
      : undefined;

  let configLabelKey: string = key;

  if (
    key in payload &&
    typeof payload[key as keyof typeof payload] === 'string'
  ) {
    configLabelKey = payload[key as keyof typeof payload] as string;
  } else if (
    payloadPayload &&
    key in payloadPayload &&
    typeof payloadPayload[key as keyof typeof payloadPayload] === 'string'
  ) {
    configLabelKey = payloadPayload[
      key as keyof typeof payloadPayload
    ] as string;
  }

  return configLabelKey in config
    ? config[configLabelKey]
    : config[key as keyof typeof config];
}

export {
  ChartContainer,
  ChartTooltip,
  ChartTooltipContent,
  ChartLegend,
  ChartLegendContent,
  ChartStyle,
};

import * as React from 'react';
import * as DialogPrimitive from '@radix-ui/react-dialog';
import { X } from 'lucide-react';
import { useMobileViewport } from '@/hooks/useMobileViewport';

import { cn } from '@/lib/utils';

const Dialog = DialogPrimitive.Root;

const DialogTrigger = DialogPrimitive.Trigger;

const DialogPortal = DialogPrimitive.Portal;

const DialogClose = DialogPrimitive.Close;

const DialogOverlay = React.forwardRef<
  React.ElementRef<typeof DialogPrimitive.Overlay>,
  React.ComponentPropsWithoutRef<typeof DialogPrimitive.Overlay>
>(({ className, ...props }, ref) => (
  <DialogPrimitive.Overlay
    ref={ref}
    className={cn(
      'fixed inset-0 z-50 backdrop-blur-sm data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0',
      className
    )}
    style={{
      backgroundColor: 'rgba(0, 0, 0, 0.8)',
    }}
    {...props}
  />
));
DialogOverlay.displayName = DialogPrimitive.Overlay.displayName;

const DialogContent = React.forwardRef<
  React.ElementRef<typeof DialogPrimitive.Content>,
  React.ComponentPropsWithoutRef<typeof DialogPrimitive.Content>
>(({ className, children, ...props }, ref) => {
  const { keyboardVisible, availableHeight } = useMobileViewport();
  const modalRef = React.useRef<HTMLDivElement>(null);
  const [modalHeight, setModalHeight] = React.useState<number>(0);
  const [isStandalone, setIsStandalone] = React.useState<boolean>(false);
  const [safeAreaTop, setSafeAreaTop] = React.useState<number>(0);

  // Detect PWA standalone mode and safe area inset top
  React.useEffect(() => {
    const standalone =
      'standalone' in window.navigator ? window.navigator.standalone : false;
    setIsStandalone(!!standalone);

    const sat =
      parseInt(
        getComputedStyle(document.documentElement).getPropertyValue('--sat')
      ) || 0;
    setSafeAreaTop(sat);
  }, []);

  // Update modal height when content changes
  React.useEffect(() => {
    if (modalRef.current) {
      const height = modalRef.current.offsetHeight;
      setModalHeight(height);
    }
  }, [children]);

  const getModalPosition = React.useCallback(() => {
    if (!keyboardVisible) {
      return {
        top: '50%',
        transform: 'translate(-50%, -50%)',
        maxHeight: '90vh',
      };
    }

    // When keyboard is visible, position the top of the modal
    const modalMaxHeight = Math.min(availableHeight * 0.7, 400);
    let topPosition = availableHeight * (isStandalone ? 0.55 : 0.61); // 55% in PWA mode, 61% in Safari

    // Adjust for PWA standalone mode (account for safe area inset top)
    if (isStandalone) {
      topPosition += safeAreaTop; // Add safe area inset top to position below the status bar
    }

    return {
      top: `${Math.max(40, topPosition)}px`,
      transform: 'translateX(-50%)',
      maxHeight: `${modalMaxHeight}px`,
    };
  }, [keyboardVisible, availableHeight, isStandalone, safeAreaTop]);

  const modalStyle = getModalPosition();

  // Ensure position updates on every render when keyboard is visible
  React.useEffect(() => {
    const updatePosition = () => {
      if (keyboardVisible && modalRef.current) {
        const updatedStyle = getModalPosition();
        modalRef.current.style.top = updatedStyle.top;
        modalRef.current.style.transform = updatedStyle.transform;
        modalRef.current.style.maxHeight = updatedStyle.maxHeight;
      }
    };

    updatePosition(); // Initial update

    // Add a small delay to ensure DOM is fully rendered
    const timer = setTimeout(updatePosition, 100);

    // Update on window resize (e.g., keyboard show/hide)
    window.addEventListener('resize', updatePosition);
    if (window.visualViewport) {
      window.visualViewport.addEventListener('resize', updatePosition);
    }

    return () => {
      clearTimeout(timer);
      window.removeEventListener('resize', updatePosition);
      if (window.visualViewport) {
        window.visualViewport.removeEventListener('resize', updatePosition);
      }
    };
  }, [
    keyboardVisible,
    availableHeight,
    modalHeight,
    getModalPosition,
    isStandalone,
    safeAreaTop,
  ]);

  return (
    <DialogPortal>
      <DialogOverlay />
      <DialogPrimitive.Content
        ref={modalRef}
        className={cn(
          'fixed left-[50%] z-50 grid w-full max-w-lg gap-4 overflow-y-auto border p-6 shadow-lg duration-200 data-[state=open]:animate-in data-[state=closed]:animate-out data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0 data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95 data-[state=closed]:slide-out-to-left-1/2 data-[state=open]:slide-in-from-left-1/2 sm:rounded-xl',
          keyboardVisible &&
            'data-[state=closed]:slide-out-to-top-[40px] data-[state=open]:slide-in-from-top-[40px]',
          !keyboardVisible &&
            'data-[state=closed]:slide-out-to-top-[48%] data-[state=open]:slide-in-from-top-[48%]',
          className
        )}
        style={{
          backgroundColor: '#1a1a1a',
          color: '#ffffff',
          borderColor: '#404040',
          ...modalStyle,
        }}
        {...props}
      >
        {children}
        <DialogPrimitive.Close className="absolute right-4 top-4 rounded-full opacity-70 ring-offset-background transition-opacity hover:opacity-100 focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2 disabled:pointer-events-none data-[state=open]:bg-accent data-[state=open]:text-muted-foreground">
          <X className="h-4 w-4" style={{ color: '#ffffff' }} />
          <span className="sr-only">Close</span>
        </DialogPrimitive.Close>
      </DialogPrimitive.Content>
    </DialogPortal>
  );
});
DialogContent.displayName = DialogPrimitive.Content.displayName;

const DialogHeader = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn(
      'flex flex-col space-y-1.5 text-center sm:text-left',
      className
    )}
    {...props}
  />
);
DialogHeader.displayName = 'DialogHeader';

const DialogFooter = ({
  className,
  ...props
}: React.HTMLAttributes<HTMLDivElement>) => (
  <div
    className={cn(
      'flex flex-col-reverse sm:flex-row sm:justify-end sm:space-x-2',
      className
    )}
    {...props}
  />
);
DialogFooter.displayName = 'DialogFooter';

const DialogTitle = React.forwardRef<
  React.ElementRef<typeof DialogPrimitive.Title>,
  React.ComponentPropsWithoutRef<typeof DialogPrimitive.Title>
>(({ className, ...props }, ref) => (
  <DialogPrimitive.Title
    ref={ref}
    className={cn(
      'text-lg font-semibold leading-none tracking-tight',
      className
    )}
    style={{ color: '#ffffff' }}
    {...props}
  />
));
DialogTitle.displayName = DialogPrimitive.Title.displayName;

const DialogDescription = React.forwardRef<
  React.ElementRef<typeof DialogPrimitive.Description>,
  React.ComponentPropsWithoutRef<typeof DialogPrimitive.Description>
>(({ className, ...props }, ref) => (
  <DialogPrimitive.Description
    ref={ref}
    className={cn('text-sm', className)}
    style={{ color: '#a3a3a3' }}
    {...props}
  />
));
DialogDescription.displayName = DialogPrimitive.Description.displayName;

export {
  Dialog,
  DialogPortal,
  DialogOverlay,
  DialogClose,
  DialogTrigger,
  DialogContent,
  DialogHeader,
  DialogFooter,
  DialogTitle,
  DialogDescription,
};

import React, { useRef, useEffect, useState } from 'react';
import { useSpring, animated } from '@react-spring/web';
import { LucideIcon } from 'lucide-react';
import { cn } from '@/lib/utils';
import {
  computeNavbarColors,
  setupThemeObserver,
} from '@/lib/utils/navbarColors';
import {
  calculateActiveButtonBounds,
  setButtonRef,
} from '@/lib/utils/navbarGeometry';

interface NavItem {
  name: string;
  value: string;
  icon: LucideIcon;
}

interface SimpleNavbarProps {
  items: NavItem[];
  activeItem: string;
  onItemChange: (value: string) => void;
  className?: string;
}

export function SimpleNavbar({
  items,
  activeItem,
  onItemChange,
  className,
}: SimpleNavbarProps) {
  const containerRef = useRef<HTMLDivElement>(null);
  const buttonRefs = useRef<(HTMLButtonElement | null)[]>([]);
  const [activeButtonBounds, setActiveButtonBounds] = useState({
    x: 0,
    width: 0,
  });
  const [isInitialized, setIsInitialized] = useState(false);
  const [computedColors, setComputedColors] = useState(() =>
    computeNavbarColors()
  );

  // Get computed CSS variable values and detect theme
  useEffect(() => {
    const updateColors = () => {
      setComputedColors(computeNavbarColors());
    };

    // Get colors on mount and when theme changes
    updateColors();

    // Listen for theme changes
    const cleanup = setupThemeObserver(updateColors);

    return cleanup;
  }, []);

  // Calculate active button position with proper timing
  const updateActiveButtonBounds = () => {
    const activeIndex = items.findIndex((item) => item.value === activeItem);
    const container = containerRef.current;

    if (activeIndex >= 0 && container) {
      const containerPadding = 8;
      const newBounds = calculateActiveButtonBounds(
        activeIndex,
        buttonRefs.current,
        container,
        containerPadding
      );

      setActiveButtonBounds(newBounds);

      if (!isInitialized) {
        setIsInitialized(true);
      }
    }
  };

  // Initialize and update on changes
  useEffect(() => {
    // Use requestAnimationFrame to ensure DOM is ready
    const frame = requestAnimationFrame(() => {
      updateActiveButtonBounds();
    });

    return () => {
      cancelAnimationFrame(frame);
    };
  }, [activeItem, items, computedColors]);

  // Handle window resize
  useEffect(() => {
    const handleResize = () => {
      updateActiveButtonBounds();
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, [activeItem]);

  // Spring animation for active indicator line
  const indicatorLineSpring = useSpring({
    transform: `translateX(${activeButtonBounds.x}px)`,
    width: 40,
    opacity: isInitialized ? 1 : 0,
    config: {
      tension: 300,
      friction: 30,
    },
  });

  // Spring animation for button background
  const buttonBackgroundSpring = useSpring({
    transform: `translateX(${activeButtonBounds.x}px)`,
    width: activeButtonBounds.width,
    opacity: isInitialized ? 1 : 0,
    config: {
      tension: 300,
      friction: 30,
    },
  });

  // Spring animation for glow effect
  const glowSpring = useSpring({
    transform: `translateX(${activeButtonBounds.x - 8}px)`,
    width: activeButtonBounds.width + 16,
    opacity: isInitialized ? 1 : 0,
    config: {
      tension: 250,
      friction: 35,
    },
  });

  return (
    <div className={cn('flex w-full justify-center', className)}>
      <div
        ref={containerRef}
        className="relative flex items-center gap-1 rounded-full border border-gray-300 bg-gray-50/80 p-2 shadow-lg backdrop-blur-md dark:border-gray-600 dark:bg-gray-900/90"
      >
        {/* Active indicator line above button */}
        <animated.div
          style={{
            position: 'absolute',
            top: '-4px',
            left: '12px',
            height: '4px',
            borderRadius: '9999px',
            zIndex: 20,
            filter: 'blur(1px)',
            backgroundColor: computedColors.indicatorColor,
            boxShadow: computedColors.isDarkMode
              ? '0 0 8px rgba(255, 255, 255, 0.4)'
              : `0 0 8px ${computedColors.primaryGlow}`,
            ...indicatorLineSpring,
          }}
        />

        {/* Button background highlight */}
        <animated.div
          style={{
            position: 'absolute',
            left: '8px',
            height: '38px',
            borderRadius: '9999px',
            zIndex: 5,
            backgroundColor: computedColors.highlightColor,
            boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
            ...buttonBackgroundSpring,
          }}
        />

        {/* Ambient glow effect */}
        <animated.div
          style={{
            position: 'absolute',
            top: '0px',
            left: '0px',
            height: '48px',
            borderRadius: '9999px',
            zIndex: 0,
            backgroundColor: computedColors.primaryVeryLight,
            filter: 'blur(12px)',
            boxShadow: `0 0 20px ${computedColors.primaryGlow}`,
            ...glowSpring,
          }}
        />

        {items.map((item, index) => {
          const Icon = item.icon;
          const isActive = activeItem === item.value;

          return (
            <button
              key={item.value}
              ref={(el) => setButtonRef(buttonRefs, index, el)}
              onClick={() => onItemChange(item.value)}
              className={cn(
                'relative z-10 min-w-[48px] cursor-pointer rounded-full border-none bg-transparent px-4 py-2 text-sm font-medium transition-all duration-200',
                isActive
                  ? 'text-white dark:text-white'
                  : 'text-gray-600 hover:text-gray-900 dark:text-gray-400 dark:hover:text-gray-200'
              )}
            >
              <span className="relative z-20 flex items-center justify-center">
                <Icon size={18} strokeWidth={2.5} />
              </span>
            </button>
          );
        })}
      </div>
    </div>
  );
}

import { Moon, Sun } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { useTheme } from '@/contexts/ThemeContext';

export function ThemeToggle() {
  const { theme, setTheme, actualTheme } = useTheme();

  const handleToggle = () => {
    setTheme(actualTheme === 'dark' ? 'light' : 'dark');
  };

  return (
    <Button
      variant="ghost"
      size="sm"
      onClick={handleToggle}
      className="h-8 w-8 p-0"
      aria-label={`Switch to ${actualTheme === 'dark' ? 'light' : 'dark'} theme`}
      title={`Switch to ${actualTheme === 'dark' ? 'light' : 'dark'} theme`}
    >
      {actualTheme === 'dark' ? (
        <Sun className="h-4 w-4 text-foreground" aria-hidden="true" />
      ) : (
        <Moon className="h-4 w-4 text-foreground" aria-hidden="true" />
      )}
      <span className="sr-only">
        {actualTheme === 'dark'
          ? 'Switch to light theme'
          : 'Switch to dark theme'}
      </span>
    </Button>
  );
}

