import {
  createContext,
  useContext,
  useState,
  useEffect,
  ReactNode,
} from 'react';
import { supabase } from '@/integrations/supabase/client';
import { toast } from '@/lib/toast';
import { User, Session } from '@supabase/supabase-js';
import { getCurrentUser } from '@/integrations/supabase/api/users.api';

interface AuthContextType {
  user: User | null;
  session: Session | null;
  loading: boolean;
  error: Error | null;
  signOut: () => Promise<void>;
  refreshSession: () => Promise<void>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: ReactNode }) {
  const [user, setUser] = useState<User | null>(null);
  const [session, setSession] = useState<Session | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<Error | null>(null);

  // Clean up auth state utility
  const cleanupAuthState = () => {
    Object.keys(localStorage).forEach((key) => {
      if (key.startsWith('supabase.auth.') || key.includes('sb-')) {
        localStorage.removeItem(key);
      }
    });
    Object.keys(sessionStorage || {}).forEach((key) => {
      if (key.startsWith('supabase.auth.') || key.includes('sb-')) {
        sessionStorage.removeItem(key);
      }
    });
  };

  // Refresh session function
  const refreshSession = async () => {
    try {
      const { data, error } = await supabase.auth.refreshSession();
      if (error) throw error;

      setSession(data.session);
      setUser(data.session?.user ?? null);
    } catch (error: unknown) {
      console.error('Session refresh error:', error);
      if (error instanceof Error) {
        setError(error);
      }
    }
  };

  // Sign out function with enhanced cleanup
  const signOut = async () => {
    try {
      setLoading(true);

      // Clean up auth state first
      cleanupAuthState();

      // Attempt global sign out
      try {
        await supabase.auth.signOut({ scope: 'global' });
      } catch (err) {
        console.warn(
          'Global sign out failed, continuing with local cleanup:',
          err
        );
      }

      // Clear local state
      setUser(null);
      setSession(null);
      setError(null);

      toast.success('Signed out successfully');

      // Force page reload for clean state
      setTimeout(() => {
        window.location.href = '/auth';
      }, 500);
    } catch (error: unknown) {
      console.error('Sign-out error:', error);
      toast.error('Failed to sign out');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    let mounted = true;

    // Set up auth state listener FIRST
    const {
      data: { subscription },
    } = supabase.auth.onAuthStateChange(async (event, session) => {
      console.log('Auth state change:', event, session?.user?.id);

      if (!mounted) return;

      // Update session and user state
      setSession(session);
      setUser(session?.user ?? null);

      // Handle different auth events
      if (event === 'SIGNED_IN' && session?.user) {
        // Defer any additional data fetching to prevent deadlocks
        setTimeout(() => {
          if (mounted) {
            setError(null);
            setLoading(false);
          }
        }, 0);
      } else if (event === 'SIGNED_OUT') {
        setUser(null);
        setSession(null);
        setError(null);
        setLoading(false);
      } else if (event === 'TOKEN_REFRESHED') {
        console.log('Token refreshed successfully');
      }
    });

    // THEN check for existing session
    const initializeAuth = async () => {
      try {
        const {
          data: { session },
          error,
        } = await supabase.auth.getSession();

        if (error) {
          console.error('Error getting session:', error);
          setError(error);
        }

        if (mounted) {
          setSession(session);
          setUser(session?.user ?? null);
        }
      } catch (error: unknown) {
        console.error('Auth initialization error:', error);
        if (mounted && error instanceof Error) {
          setError(error);
        }
      } finally {
        if (mounted) {
          setLoading(false);
        }
      }
    };

    initializeAuth();

    return () => {
      mounted = false;
      subscription.unsubscribe();
    };
  }, []);

  return (
    <AuthContext.Provider
      value={{
        user,
        session,
        loading,
        error,
        signOut,
        refreshSession,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

import React, { createContext, useContext, useEffect, useState } from 'react';

type Theme = 'dark' | 'light' | 'system';

interface ThemeContextType {
  theme: Theme;
  setTheme: (theme: Theme) => void;
  actualTheme: 'dark' | 'light';
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);

export function ThemeProvider({ children }: { children: React.ReactNode }) {
  const [theme, setTheme] = useState<Theme>('dark'); // Default to dark
  const [actualTheme, setActualTheme] = useState<'dark' | 'light'>('dark');

  useEffect(() => {
    // Get stored theme preference or default to dark
    const storedTheme = localStorage.getItem('theme') as Theme;
    if (storedTheme) {
      setTheme(storedTheme);
    } else {
      // Set dark as default and store it
      localStorage.setItem('theme', 'dark');
    }
  }, []);

  useEffect(() => {
    const root = window.document.documentElement;

    // Remove existing theme classes
    root.classList.remove('light', 'dark');

    let effectiveTheme: 'dark' | 'light';

    if (theme === 'system') {
      effectiveTheme = window.matchMedia('(prefers-color-scheme: dark)').matches
        ? 'dark'
        : 'light';
    } else {
      effectiveTheme = theme;
    }

    // Apply the theme class
    root.classList.add(effectiveTheme);
    setActualTheme(effectiveTheme);

    // Store the preference
    localStorage.setItem('theme', theme);
  }, [theme]);

  // Listen for system theme changes when using system preference
  useEffect(() => {
    if (theme !== 'system') return;

    const mediaQuery = window.matchMedia('(prefers-color-scheme: dark)');
    const handleChange = () => {
      const root = window.document.documentElement;
      root.classList.remove('light', 'dark');

      const effectiveTheme = mediaQuery.matches ? 'dark' : 'light';
      root.classList.add(effectiveTheme);
      setActualTheme(effectiveTheme);
    };

    mediaQuery.addEventListener('change', handleChange);
    return () => mediaQuery.removeEventListener('change', handleChange);
  }, [theme]);

  return (
    <ThemeContext.Provider value={{ theme, setTheme, actualTheme }}>
      {children}
    </ThemeContext.Provider>
  );
}

export function useTheme() {
  const context = useContext(ThemeContext);
  if (context === undefined) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
}

import React, { useMemo } from 'react';
import { useSpring, animated, config } from '@react-spring/web';
import { TaskStatus } from '@/types';
import TimerRing from './timer/TimerRing';
import TimerDisplay from './timer/TimerDisplay';
import { useTaskUIContext } from '@/features/tasks/context/TaskUIContext';
import {
  Tooltip,
  TooltipProvider,
  TooltipTrigger,
} from '@/components/ui/tooltip';
import TimerTooltip from '@/features/tasks/components/TimerTooltip';
import { setupAnimationVariables, getSpringConfig } from '@/lib/animationUtils';
import { useCountdown } from '@/hooks/useCountdown';
import { useMotionPreferences } from '@/hooks/useMotionPreferences';

/**
 * CountdownTimer component displays a circular timer that visualizes the time remaining
 * until a task's due date.
 *
 * Features:
 * - Visually displays time left as a circular progress indicator
 * - Adapts display based on task status (pending, complete, overdue)
 * - Shows different styling based on priority
 * - Resizes based on mobile/desktop view
 * - Provides detailed due date information via tooltip
 * - Respects user motion preferences for accessibility
 *
 * @param dueDate - The due date of the task as an ISO string
 * @param status - The current status of the task
 * @param size - The size of the timer in pixels (default: 48)
 * @param priority - The priority of the task (default: "medium")
 */
interface CountdownTimerProps {
  dueDate: string | null;
  status: TaskStatus;
  size?: number;
  priority?: 'low' | 'medium' | 'high';
}

const AnimatedDiv = animated.div;

function CountdownTimer({
  dueDate,
  status,
  size = 48,
  priority = 'medium',
}: CountdownTimerProps) {
  const { isMobile } = useTaskUIContext();
  const { shouldReduceMotion, getAnimationConfig } = useMotionPreferences();

  // Calculate and memoize size-related values to avoid recalculation
  const { dynamicSize, radius, circumference } = useMemo(() => {
    const dynamicSize = isMobile
      ? priority === 'high'
        ? size * 1.1
        : priority === 'low'
          ? size * 0.7
          : size * 0.9
      : priority === 'high'
        ? size * 1.2
        : priority === 'low'
          ? size * 0.8
          : size;

    const radius = dynamicSize / 2 - 4;
    const circumference = 2 * Math.PI * radius;

    return { dynamicSize, radius, circumference };
  }, [isMobile, priority, size]);

  // Use our custom hook to manage countdown logic
  const { timeDisplay, dashOffset, tooltipContent, ariaLabel } = useCountdown(
    dueDate,
    status,
    circumference
  );

  // Animation configuration based on motion preferences
  const springConfig = useMemo(
    () =>
      getAnimationConfig(
        { tension: 120, friction: 14 },
        { tension: 300, friction: 30 }
      ),
    [shouldReduceMotion, getAnimationConfig]
  );

  // Animation for the timer ring with motion preference support
  const { strokeDashoffset } = useSpring({
    strokeDashoffset: dashOffset,
    config: springConfig,
    immediate:
      status === 'complete' ||
      status === 'overdue' ||
      !dueDate ||
      shouldReduceMotion,
  });

  // Apply GPU acceleration for better performance on non-reduced motion
  const containerStyles = useMemo(
    () => ({
      width: dynamicSize,
      height: dynamicSize,
      ...(shouldReduceMotion ? {} : { transform: 'translateZ(0)' }),
    }),
    [dynamicSize, shouldReduceMotion]
  );

  return (
    <TooltipProvider>
      <Tooltip delayDuration={300}>
        <TooltipTrigger asChild>
          <AnimatedDiv
            role="timer"
            tabIndex={0}
            aria-label={ariaLabel}
            className={`timer-container relative rounded-full focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-primary focus-visible:ring-offset-2 ${
              status === 'pending' &&
              Number(timeDisplay) === 0 &&
              !shouldReduceMotion
                ? 'animate-pulse-subtle'
                : ''
            } ${shouldReduceMotion ? 'reduce-motion-duration' : 'gpu-accelerated'}`}
            style={containerStyles}
          >
            <TimerRing
              size={dynamicSize}
              radius={radius}
              circumference={circumference}
              strokeDashoffset={strokeDashoffset}
              status={status}
            />
            <TimerDisplay
              size={dynamicSize}
              status={status}
              timeDisplay={timeDisplay}
            />
          </AnimatedDiv>
        </TooltipTrigger>
        <TimerTooltip tooltipContent={tooltipContent} status={status} />
      </Tooltip>
    </TooltipProvider>
  );
}

export default CountdownTimer;

