import * as React from 'react';

import type { ToastActionElement, ToastProps } from '@/components/ui/toast';

const TOAST_LIMIT = 5;
const TOAST_REMOVE_DELAY = 1000000;

type ToasterToast = ToastProps & {
  id: string;
  title?: React.ReactNode;
  description?: React.ReactNode;
  action?: ToastActionElement;
};

const actionTypes = {
  ADD_TOAST: 'ADD_TOAST',
  UPDATE_TOAST: 'UPDATE_TOAST',
  DISMISS_TOAST: 'DISMISS_TOAST',
  REMOVE_TOAST: 'REMOVE_TOAST',
} as const;

let count = 0;

function genId() {
  count = (count + 1) % Number.MAX_VALUE;
  return count.toString();
}

type ActionType = typeof actionTypes;

type Action =
  | {
      type: ActionType['ADD_TOAST'];
      toast: ToasterToast;
    }
  | {
      type: ActionType['UPDATE_TOAST'];
      toast: Partial<ToasterToast>;
    }
  | {
      type: ActionType['DISMISS_TOAST'];
      toastId?: ToasterToast['id'];
    }
  | {
      type: ActionType['REMOVE_TOAST'];
      toastId?: ToasterToast['id'];
    };

interface State {
  toasts: ToasterToast[];
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>();

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return;
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId);
    dispatch({
      type: 'REMOVE_TOAST',
      toastId: toastId,
    });
  }, TOAST_REMOVE_DELAY);

  toastTimeouts.set(toastId, timeout);
};

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case 'ADD_TOAST':
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      };

    case 'UPDATE_TOAST':
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      };

    case 'DISMISS_TOAST': {
      const { toastId } = action;

      // ! Side effects ! - This could be extracted into a dismissToast() action
      if (toastId) {
        addToRemoveQueue(toastId);
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id);
        });
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      };
    }
    case 'REMOVE_TOAST':
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        };
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      };
  }
};

const listeners: Array<(state: State) => void> = [];

let memoryState: State = { toasts: [] };

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action);
  listeners.forEach((listener) => {
    listener(memoryState);
  });
}

type Toast = Omit<ToasterToast, 'id'>;

function toast({ ...props }: Toast) {
  const id = genId();

  const update = (props: ToasterToast) =>
    dispatch({
      type: 'UPDATE_TOAST',
      toast: { ...props, id },
    });
  const dismiss = () => dispatch({ type: 'DISMISS_TOAST', toastId: id });

  dispatch({
    type: 'ADD_TOAST',
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss();
      },
    },
  });

  return {
    id: id,
    dismiss,
    update,
  };
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState);

  React.useEffect(() => {
    listeners.push(setState);
    return () => {
      const index = listeners.indexOf(setState);
      if (index > -1) {
        listeners.splice(index, 1);
      }
    };
  }, [state]);

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: 'DISMISS_TOAST', toastId }),
  };
}

toast.dismiss = (toastId?: string) => {
  dispatch({ type: 'DISMISS_TOAST', toastId });
};

toast.success = (message: string) => {
  toast({ title: 'Success', description: message, variant: 'default' });
};

toast.error = (message: string) => {
  toast({ title: 'Error', description: message, variant: 'destructive' });
};

export { useToast, toast };

import { useState, useEffect } from 'react';

interface MobileViewportState {
  keyboardVisible: boolean;
  viewportHeight: number;
  availableHeight: number;
  keyboardHeight: number;
  safeAreaTop: number;
  safeAreaBottom: number;
}

export function useMobileViewport(): MobileViewportState {
  const [state, setState] = useState<MobileViewportState>({
    keyboardVisible: false,
    viewportHeight: window.innerHeight,
    availableHeight: window.innerHeight,
    keyboardHeight: 0,
    safeAreaTop: 0,
    safeAreaBottom: 0,
  });

  useEffect(() => {
    const initialHeight = window.innerHeight;

    const updateViewportState = () => {
      const currentHeight = window.visualViewport?.height || window.innerHeight;
      const screenHeight = window.innerHeight;
      const keyboardHeight = Math.max(0, screenHeight - currentHeight);
      const keyboardVisible = keyboardHeight > 150; // Threshold for keyboard detection

      // Use the full visible viewport height
      const availableHeight = currentHeight;

      setState({
        keyboardVisible,
        viewportHeight: currentHeight,
        availableHeight,
        keyboardHeight,
        safeAreaTop:
          parseInt(
            getComputedStyle(document.documentElement).getPropertyValue(
              'env(safe-area-inset-top)'
            )
          ) || 0,
        safeAreaBottom:
          parseInt(
            getComputedStyle(document.documentElement).getPropertyValue(
              'env(safe-area-inset-bottom)'
            )
          ) || 0,
      });
    };

    const handleResize = () => {
      updateViewportState();
    };

    const handleVisualViewportChange = () => {
      updateViewportState();
    };

    // Listen for viewport changes
    window.addEventListener('resize', handleResize);
    if (window.visualViewport) {
      window.visualViewport.addEventListener(
        'resize',
        handleVisualViewportChange
      );
      window.visualViewport.addEventListener(
        'scroll',
        handleVisualViewportChange
      );
    }

    // Initial setup
    updateViewportState();

    return () => {
      window.removeEventListener('resize', handleResize);
      if (window.visualViewport) {
        window.visualViewport.removeEventListener(
          'resize',
          handleVisualViewportChange
        );
        window.visualViewport.removeEventListener(
          'scroll',
          handleVisualViewportChange
        );
      }
    };
  }, []);

  return state;
}

import { useState, useEffect } from 'react';

/**
 * Hook to detect and manage user motion preferences
 *
 * @returns Object containing motion preference state and utilities
 */
export function useMotionPreferences() {
  const [prefersReducedMotion, setPrefersReducedMotion] = useState(false);
  const [isLoaded, setIsLoaded] = useState(false);

  useEffect(() => {
    // Check if matchMedia is available (for SSR compatibility)
    if (typeof window === 'undefined' || !window.matchMedia) {
      setIsLoaded(true);
      return;
    }

    const mediaQuery = window.matchMedia('(prefers-reduced-motion: reduce)');

    // Set initial value
    setPrefersReducedMotion(mediaQuery.matches);
    setIsLoaded(true);

    // Listen for changes
    const handleChange = (event: MediaQueryListEvent) => {
      setPrefersReducedMotion(event.matches);
    };

    mediaQuery.addEventListener('change', handleChange);

    return () => {
      mediaQuery.removeEventListener('change', handleChange);
    };
  }, []);

  return {
    prefersReducedMotion,
    isLoaded,
    shouldReduceMotion: prefersReducedMotion,
    getAnimationConfig: (normalConfig: any, reducedConfig: any) =>
      prefersReducedMotion ? reducedConfig : normalConfig,
  };
}

import { useCallback, useRef } from 'react';
import { useRealtimeSubscription } from './useRealtimeSubscription';
import { useAuth } from '@/contexts/AuthContext';

interface RealtimeEntityConfig<T> {
  table: string;
  entityName: string;
  getUserId?: (entity: T) => string | null;
  getOwnerId?: (entity: T) => string | null;
  getTitle?: (entity: T) => string;
  onInsertNotification?: (entity: T, isForCurrentUser: boolean) => void;
  onUpdateNotification?: (oldEntity: T, newEntity: T, isForCurrentUser: boolean) => void;
  onDeleteNotification?: (entity: T, isForCurrentUser: boolean) => void;
  // New callbacks for UI feedback
  onUserAssigned?: (event: { entity: T; title: string }) => void;
  onAssignmentChanged?: (event: { oldEntity: T; newEntity: T; title: string }) => void;
}

/**
 * Generalized hook for real-time entity updates with notifications
 */
export function useRealtimeEntity<T>(config: RealtimeEntityConfig<T>) {
  const { user } = useAuth();
  const {
    table,
    entityName,
    getUserId,
    getOwnerId,
    getTitle,
    onInsertNotification,
    onUpdateNotification,
    onDeleteNotification,
    onUserAssigned,
    onAssignmentChanged,
  } = config;

  // Store the last event for UI observation
  const lastEventRef = useRef<any>(null);

  const handleEntityInsert = useCallback((payload: any) => {
    const newEntity = payload.new as T;
    const isForCurrentUser = getUserId ? getUserId(newEntity) === user?.id : false;
    const isOwnedByCurrentUser = getOwnerId ? getOwnerId(newEntity) === user?.id : false;

    // Notify UI for entities assigned to current user but not owned by them
    if (isForCurrentUser && !isOwnedByCurrentUser) {
      const title = getTitle ? getTitle(newEntity) : 'New item';
      lastEventRef.current = { type: 'userAssigned', entity: newEntity, title };
      onUserAssigned?.({ entity: newEntity, title });
    }

    onInsertNotification?.(newEntity, isForCurrentUser);
    // console.log(` New ${entityName} created:`, newEntity);
  }, [user?.id, getUserId, getOwnerId, getTitle, onInsertNotification, onUserAssigned]);

  const handleEntityUpdate = useCallback((payload: any) => {
    const updatedEntity = payload.new as T;
    const oldEntity = payload.old as T;
    const isForCurrentUser = getUserId ? getUserId(updatedEntity) === user?.id : false;
    const isOwnedByCurrentUser = getOwnerId ? getOwnerId(updatedEntity) === user?.id : false;

    // Handle assignment changes
    if (getUserId && getOwnerId) {
      const oldUserId = getUserId(oldEntity);
      const newUserId = getUserId(updatedEntity);

      if (oldUserId !== newUserId && newUserId === user?.id) {
        const title = getTitle ? getTitle(updatedEntity) : 'Item';
        lastEventRef.current = { type: 'assignmentChanged', oldEntity, newEntity: updatedEntity, title };
        onAssignmentChanged?.({ oldEntity, newEntity: updatedEntity, title });
      }
    }

    onUpdateNotification?.(oldEntity, updatedEntity, isForCurrentUser);
    // console.log(` ${entityName} updated:`, { old: oldEntity, new: updatedEntity });
  }, [user?.id, getUserId, getOwnerId, getTitle, onUpdateNotification, onAssignmentChanged]);

  const handleEntityDelete = useCallback((payload: any) => {
    const deletedEntity = payload.old as T;
    const isForCurrentUser = getUserId ? getUserId(deletedEntity) === user?.id : false;

    onDeleteNotification?.(deletedEntity, isForCurrentUser);
    // console.log(` ${entityName} deleted:`, deletedEntity);
  }, [getUserId, onDeleteNotification]);

  const { isSubscribed } = useRealtimeSubscription({
    table,
    onInsert: handleEntityInsert,
    onUpdate: handleEntityUpdate,
    onDelete: handleEntityDelete,
    invalidateQueries: [table],
  });

  return {
    isSubscribed,
    lastEvent: lastEventRef.current,
  };
}

import { useEffect, useRef, useState } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { useQueryClient } from '@tanstack/react-query';
import { useAuth } from '@/contexts/AuthContext';

interface RealtimeSubscriptionOptions {
  table: string;
  schema?: string;
  event?: 'INSERT' | 'UPDATE' | 'DELETE' | '*';
  onInsert?: (payload: any) => void;
  onUpdate?: (payload: any) => void;
  onDelete?: (payload: any) => void;
  invalidateQueries?: string[];
}

/**
 * Optimized hook for managing real-time subscriptions with proper cleanup
 */
export function useRealtimeSubscription({
  table,
  schema = 'public',
  event = '*',
  onInsert,
  onUpdate,
  onDelete,
  invalidateQueries = [],
}: RealtimeSubscriptionOptions) {
  const queryClient = useQueryClient();
  const { user } = useAuth();
  const channelRef = useRef<any>(null);
  const [isSubscribed, setIsSubscribed] = useState(false);

  useEffect(() => {
    // Only subscribe if user is authenticated
    if (!user) {
      setIsSubscribed(false);
      return;
    }

    // Create unique channel name
    const channelName = `realtime-${table}-${user.id}-${Date.now()}`;

    console.log(` Setting up real-time subscription for ${table}`);

    try {
      const channel = supabase
        .channel(channelName)
        .on(
          'postgres_changes',
          {
            event: event as any,
            schema,
            table,
          },
          (payload) => {
            console.log(` Real-time update received for ${table}:`, payload);

            // Handle different event types
            switch (payload.eventType) {
              case 'INSERT':
                onInsert?.(payload);
                break;
              case 'UPDATE':
                onUpdate?.(payload);
                break;
              case 'DELETE':
                onDelete?.(payload);
                break;
            }

            // Invalidate specified queries to refresh data
            if (invalidateQueries.length > 0) {
              invalidateQueries.forEach(queryKey => {
                queryClient.invalidateQueries({ queryKey: [queryKey] });
              });
            }
          }
        )
        .subscribe((status) => {
          console.log(` Subscription status for ${table}:`, status);

          if (status === 'SUBSCRIBED') {
            setIsSubscribed(true);
            console.log(` Successfully subscribed to ${table} changes`);
          } else if (status === 'CHANNEL_ERROR') {
            console.error(` Subscription error for ${table}`);
            setIsSubscribed(false);
          } else if (status === 'CLOSED') {
            setIsSubscribed(false);
          }
        });

      channelRef.current = channel;
    } catch (error) {
      console.error(` Failed to set up real-time subscription for ${table}:`, error);
      setIsSubscribed(false);
    }

    // Cleanup function
    return () => {
      console.log(` Cleaning up real-time subscription for ${table}`);

      if (channelRef.current) {
        try {
          supabase.removeChannel(channelRef.current);
          setIsSubscribed(false);
        } catch (error) {
          console.error('Error cleaning up channel:', error);
        }
      }
    };
  }, [user?.id, table, schema, event, onInsert, onUpdate, onDelete, queryClient, invalidateQueries]);

  return {
    isSubscribed,
    channel: channelRef.current,
  };
}

import { useCallback } from 'react';
import { z } from 'zod';
import { validateObject, validateWithError, ValidationResult, ValidationOptions } from './validationUtils';

// Object validation hook
export function useObjectValidation() {
  return {
    validate: validateObject,
    validateWithError,
  };
}

// Field validation hook
export function useFieldValidation() {
  const validateField = useCallback(
    <T>(
      schema: z.ZodSchema<T>,
      value: unknown,
      fieldName: string,
      options: ValidationOptions = {}
    ): { isValid: boolean; error?: string } => {
      const result = validateObject(schema, { [fieldName]: value }, options);
      return {
        isValid: result.isValid,
        error: result.errors[fieldName],
      };
    },
    []
  );

  const createFieldValidator = useCallback(
    <T>(schema: z.ZodSchema<T>, options: ValidationOptions = {}) => {
      return (value: unknown, fieldName: string) => {
        return validateField(schema, value, fieldName, options);
      };
    },
    [validateField]
  );

  return {
    validateField,
    createFieldValidator,
  };
}

// Deprecated: useValidation (for backward compatibility)
/**
 * @deprecated Use useObjectValidation and useFieldValidation instead.
 */
export function useValidation() {
  const objectValidation = useObjectValidation();
  const fieldValidation = useFieldValidation();
  if (process.env.NODE_ENV !== 'production') {
    // eslint-disable-next-line no-console
    console.warn('useValidation is deprecated. Use useObjectValidation and useFieldValidation instead.');
  }
  return {
    ...objectValidation,
    ...fieldValidation,
  };
}

import { supabase } from '@/integrations/supabase/client';
import { ApiError, TablesResponse } from '../types/api.types';
import { handleApiError } from '@/lib/errorHandling';

/**
 * Handles and normalizes errors from Supabase or other sources
 *
 * @param error - The error object from Supabase or other source
 * @returns A standardized ApiError object
 */
export const formatApiError = (error: unknown): ApiError => {
  // Handle PostgrestError from Supabase
  if (
    typeof error === 'object' &&
    error !== null &&
    'code' in error &&
    'message' in error
  ) {
    return {
      name: 'ApiError', // Add name property to make compatible with Error interface
      message: String(error.message),
      code: String(error.code),
      details: 'details' in error ? error.details : undefined,
      hint: 'hint' in error ? String(error.hint) : undefined,
      originalError: error as any,
    };
  }

  // Handle standard Error objects
  if (error instanceof Error) {
    return {
      name: error.name,
      message: error.message,
      originalError: error,
    };
  }

  // Handle unknown error types
  return {
    name: 'UnknownError',
    message: typeof error === 'string' ? error : 'An unknown error occurred',
    details: error,
  };
};

/**
 * Check if a user is authenticated and get their ID
 *
 * @returns A promise resolving to the authenticated user's ID
 * @throws Error if user is not authenticated
 */
export const getCurrentUserId = async (): Promise<string> => {
  const {
    data: { user },
  } = await supabase.auth.getUser();
  if (!user) throw new Error('User not authenticated');
  return user.id;
};

/**
 * Wrapper for standardized error handling in async functions
 *
 * @param requestFn - The async function to execute
 * @returns A standardized TablesResponse with data or error
 */
export const apiRequest = async <T>(
  requestFn: () => Promise<T>
): Promise<TablesResponse<T>> => {
  try {
    const data = await requestFn();
    return { data, error: null };
  } catch (error) {
    const formattedError = formatApiError(error);

    // Log error to console for debugging
    console.error('[API Error]:', formattedError);

    return { data: null, error: formattedError };
  }
};

