import { toast } from '@/hooks/use-toast';
import { ApiError } from '@/integrations/supabase/types/api.types';

/**
 * Standardized error handling utilities for the application.
 *
 * These utilities provide consistent error handling patterns
 * across different parts of the application.
 */

/**
 * Configuration for error handling
 */
export interface ErrorHandlingOptions {
  showToast?: boolean;
  logToConsole?: boolean;
  rethrow?: boolean;
}

const defaultOptions: ErrorHandlingOptions = {
  showToast: true,
  logToConsole: true,
  rethrow: false,
};

/**
 * Process API errors in a standardized way
 *
 * @param error - The error to process
 * @param customMessage - Optional custom message to display instead of the error message
 * @param options - Error handling configuration options
 */
export const handleApiError = (
  error: Error | ApiError | unknown,
  customMessage?: string,
  options: ErrorHandlingOptions = {}
): Error => {
  // Merge with default options
  const opts = { ...defaultOptions, ...options };

  let errorMessage = customMessage || 'An unexpected error occurred';
  let errorObject: Error;

  // Process different error types
  if (error instanceof Error) {
    errorMessage = customMessage || error.message;
    errorObject = error;
  } else if (
    typeof error === 'object' &&
    error !== null &&
    'message' in error &&
    typeof error.message === 'string'
  ) {
    // Handle API error objects
    errorMessage = customMessage || error.message;
    errorObject = new Error(errorMessage);

    // Copy properties from ApiError
    if ('code' in error) {
      (errorObject as any).code = error.code;
    }
    if ('details' in error) {
      (errorObject as any).details = error.details;
    }
  } else {
    // Handle unexpected error types
    errorObject = new Error(errorMessage);
    (errorObject as any).originalError = error;
  }

  // Display error toast if enabled
  if (opts.showToast) {
    toast({
      title: 'Error',
      description: errorMessage,
      variant: 'destructive',
    });
  }

  // Log error to console if enabled
  if (opts.logToConsole) {
    console.error('[Error]:', errorMessage, errorObject);
  }

  // Rethrow if required
  if (opts.rethrow) {
    throw errorObject;
  }

  return errorObject;
};

/**
 * Utility to safely execute async functions with standardized error handling
 *
 * @param asyncFn - The async function to execute
 * @param errorMessage - Optional custom error message
 * @param options - Error handling options
 * @returns Promise resolving to the function result or null if there was an error
 */
export async function safeAsync<T>(
  asyncFn: () => Promise<T>,
  errorMessage?: string,
  options?: ErrorHandlingOptions
): Promise<T | null> {
  try {
    return await asyncFn();
  } catch (error) {
    handleApiError(error, errorMessage, options);
    return null;
  }
}

/**
 * Higher-order function that wraps an async function with error handling
 *
 * @param fn - The async function to wrap
 * @param errorMessage - Optional custom error message
 * @param options - Error handling options
 * @returns A wrapped function with error handling
 */
export function withErrorHandling<T extends (...args: any[]) => Promise<any>>(
  fn: T,
  errorMessage?: string,
  options?: ErrorHandlingOptions
): (...args: Parameters<T>) => Promise<ReturnType<T> | null> {
  return async (...args: Parameters<T>): Promise<ReturnType<T> | null> => {
    try {
      return await fn(...args);
    } catch (error) {
      handleApiError(error, errorMessage, options);
      return null;
    }
  };
}

/**
 * Compresses and resizes a photo for upload using a Web Worker
 *
 * This utility function takes a File object (image), sends it to a Web Worker for processing,
 * which compresses it and resizes it to reduce file size while maintaining reasonable quality.
 * This implementation prevents blocking the main thread during image processing.
 *
 * @param file - The image File object to process
 * @param maxWidth - Maximum width of the processed image (default: 1200px)
 * @param maxHeight - Maximum height of the processed image (default: 1200px)
 * @param quality - JPEG compression quality (0-1, default: 0.85)
 * @returns A Promise that resolves to a processed File object
 */
export async function compressAndResizePhoto(
  file: File,
  maxWidth = 1200,
  maxHeight = 1200,
  quality = 0.85
): Promise<File> {
  return new Promise((resolve, reject) => {
    // Create a worker only when needed
    const worker = new Worker(
      new URL('../workers/imageProcessorWorker.js', import.meta.url),
      { type: 'module' }
    );

    // Handle the worker response
    worker.onmessage = (event) => {
      // Terminate worker after use
      worker.terminate();

      if (event.data.success) {
        // Convert blob back to File object with original name
        const processedFile = new File([event.data.processedFile], file.name, {
          type: 'image/jpeg',
          lastModified: Date.now(),
        });

        resolve(processedFile);
      } else {
        reject(new Error(event.data.error || 'Image processing failed'));
      }
    };

    // Handle worker errors
    worker.onerror = (error) => {
      worker.terminate();
      reject(new Error('Worker error: ' + error.message));
    };

    // Send the file to the worker
    worker.postMessage({
      file,
      maxWidth,
      maxHeight,
      quality,
    });
  });
}

/**
 * Fallback function for environments where Web Workers are not supported
 */
export async function compressAndResizePhotoFallback(
  file: File,
  maxWidth = 1200,
  maxHeight = 1200,
  quality = 0.85
): Promise<File> {
  return new Promise((resolve, reject) => {
    try {
      // Create an image object from the file
      const img = new Image();
      img.src = URL.createObjectURL(file);

      img.onload = () => {
        // Calculate the new dimensions
        let width = img.width;
        let height = img.height;

        // Scale down if either dimension exceeds the maximum
        if (width > maxWidth || height > maxHeight) {
          const ratio = Math.min(maxWidth / width, maxHeight / height);
          width = Math.floor(width * ratio);
          height = Math.floor(height * ratio);
        }

        // Create a canvas and draw the resized image
        const canvas = document.createElement('canvas');
        canvas.width = width;
        canvas.height = height;

        const ctx = canvas.getContext('2d');
        if (!ctx) {
          reject(new Error('Could not get canvas context'));
          return;
        }

        ctx.drawImage(img, 0, 0, width, height);

        // Convert the canvas to a Blob
        canvas.toBlob(
          (blob) => {
            if (!blob) {
              reject(new Error('Failed to create blob from image'));
              return;
            }

            // Create a new File object from the blob
            const processedFile = new File([blob], file.name, {
              type: 'image/jpeg',
              lastModified: Date.now(),
            });

            // Clean up the object URL
            URL.revokeObjectURL(img.src);

            resolve(processedFile);
          },
          'image/jpeg',
          quality
        );
      };

      img.onerror = () => {
        URL.revokeObjectURL(img.src);
        reject(new Error('Failed to load image'));
      };
    } catch (error) {
      reject(error);
    }
  });
}

/**
 * Feature detection to determine if Web Workers are supported
 */
export const supportsWebWorker = (): boolean => {
  try {
    return (
      typeof Worker !== 'undefined' && typeof OffscreenCanvas !== 'undefined'
    );
  } catch (e) {
    return false;
  }
};

