    results.push({
      operation: 'CREATE Task',
      success: true,
      data: data,
      duration: Date.now() - createStart,
    });
  } catch (error) {
    results.push({
      operation: 'CREATE Task',
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error',
      duration: Date.now() - Date.now(),
    });
  }

  // Test 2: Read Task
  if (testTaskId) {
