      results.push({
        operation: 'READ Task',
        success: true,
        data: data,
        duration: Date.now() - readStart,
      });
    } catch (error) {
      results.push({
        operation: 'READ Task',
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error',
        duration: Date.now() - Date.now(),
      });
    }
  }

