  if (error || !task) {
    return (
      <div className="container py-8">
        <Button
          variant="ghost"
          size="sm"
          onClick={() => navigate(-1)}
          className="mb-4"
        >
          <ArrowLeft size={16} className="mr-2" /> Back
        </Button>
        <div className="rounded-lg bg-red-50 p-4 text-red-600">
