        {task.parent_task && (
          <div className="border-t pt-4">
            <h3 className="mb-3 text-lg font-medium">Follows Up On</h3>
            <div className="rounded-lg bg-gray-50 p-4">
              <h4 className="font-medium">{task.parent_task.title}</h4>
              {task.parent_task.description && (
